parts:
{
  "name": "UBX-RXM-RLM-SHORT",
  "class_id": "0x02",
  "message_id": "0x59",
  "description": "This message contains the contents of any Galileo Search and Rescue (SAR) Short Return Link Message detected by the receiver.",
  "message_type": "output",
  "payload": {
    "length": {
      "fixed": 16
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)"
      },
      {
        "name": "type",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Message type (0x01 for Short-RLM)"
      },
      {
        "name": "svId",
        "byte_offset": 2,
        "data_type": "U1",
        "description": "Identifier of transmitting satellite (see Satellite Numbering)"
      },
      {
        "name": "reserved0",
        "byte_offset": 3,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "beacon",
        "byte_offset": 4,
        "data_type": {
          "array_of": "U1",
          "count": 8
        },
        "description": "Beacon identifier (60 bits), with bytes ordered by earliest transmitted (most significant) first. Top four bits of first byte are zero."
      },
      {
        "name": "message",
        "byte_offset": 12,
        "data_type": "U1",
        "description": "Message code (4 bits)"
      },
      {
        "name": "params",
        "byte_offset": 13,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Parameters (16 bits), with bytes ordered by earliest transmitted (most significant) first."
      },
      {
        "name": "reserved1",
        "byte_offset": 15,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
