  "message_type": "input",
  "payload": {
    "length": {
      "fixed": 40
    },
    "fields": [
      {
        "name": "type",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message type (0x02 for this version)"
      },
      {
        "name": "version",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)"
      },
      {
        "name": "svId",
        "byte_offset": 2,
        "data_type": "U1",
        "description": "BeiDou satellite identifier (see Satellite Numbering)"
      },
      {
        "name": "reserved0",
        "byte_offset": 3,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "Wna",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "Almanac Week Number",
        "unit": "week"
      },
      {
        "name": "toa",
        "byte_offset": 5,
        "data_type": "U1",
        "description": "Almanac reference time",
        "unit": "s",
        "scale": {
          "raw": "2^12",
          "multiplier": 4096
        }
      },
      {
        "name": "deltaI",
        "byte_offset": 6,
        "data_type": "I2",
        "description": "Almanac correction of orbit reference inclination at reference time",
        "unit": "semi-circles",
        "scale": {
          "raw": "2^-19",
          "multiplier": 0.0000019073486328125
        }
      },
      {
        "name": "sqrtA",
        "byte_offset": 8,
        "data_type": "U4",
        "description": "Almanac square root of semi-major axis",
        "unit": "m^0.5",
        "scale": {
          "raw": "2^-11",
          "multiplier": 0.00048828125
        }
      },
      {
        "name": "e",
        "byte_offset": 12,
        "data_type": "U4",
        "description": "Almanac eccentricity",
        "scale": {
          "raw": "2^-21",
          "multiplier": 0.000000476837158203125
        }
      },
      {
        "name": "omega",
        "byte_offset": 16,
        "data_type": "I4",
        "description": "Almanac argument of perigee",
        "unit": "semi-circles",
        "scale": {
          "raw": "2^-23",
          "multiplier": 0.00000011920928955078125
        }
      },
      {
        "name": "M0",
        "byte_offset": 20,
        "data_type": "I4",
        "description": "Almanac mean anomaly at reference time",
        "unit": "semi-circles",
        "scale": {
          "raw": "2^-23",
          "multiplier": 0.00000011920928955078125
        }
      },
      {
        "name": "Omega0",
        "byte_offset": 24,
        "data_type": "I4",
        "description": "Almanac longitude of ascending node of orbit plane at computed according to reference time",
        "unit": "semi-circles",
        "scale": {
          "raw": "2^-23",
          "multiplier": 0.00000011920928955078125
        }
      },
      {
        "name": "omegaDot",
        "byte_offset": 28,
        "data_type": "I4",
        "description": "Almanac rate of right ascension",
        "unit": "semi-circles/s",
        "scale": {
          "raw": "2^-38",
          "multiplier": 0.000000000003637978807091713
        }
      },
      {
        "name": "a0",
        "byte_offset": 32,
        "data_type": "I2",
        "description": "Almanac satellite clock bias",
        "unit": "S",
        "scale": {
          "raw": "2^-20",
          "multiplier": 0.00000095367431640625
        }
      },
      {
        "name": "a1",
        "byte_offset": 34,
        "data_type": "I2",
        "description": "Almanac satellite clock rate",
        "unit": "s/s",
        "scale": {
          "raw": "2^-38",
          "multiplier": 0.000000000003637978807091713
        }
      },
      {
        "name": "reserved1",
        "byte_offset": 36,
        "data_type": {
          "array_of": "U1",
          "count": 4
        },
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}

