parts:
{
  "name": "UBX-NAV-TIMEUTC",
  "class_id": "0x01",
  "message_id": "0x21",
  "description": "UTC time solution",
  "message_type": "output",
  "payload": {
    "length": {
      "fixed": 20
    },
    "fields": [
      {
        "name": "iTOW",
        "byte_offset": 0,
        "data_type": "U4",
        "description": "GPS time of week of the navigation epoch. See section iTOW timestamps in the integration manual for details.",
        "unit": "ms"
      },
      {
        "name": "tAcc",
        "byte_offset": 4,
        "data_type": "U4",
        "description": "Time accuracy estimate (UTC)",
        "unit": "ns"
      },
      {
        "name": "nano",
        "byte_offset": 8,
        "data_type": "I4",
        "description": "Fraction of second, range -1e9.. 1e9 (UTC)",
        "unit": "ns"
      },
      {
        "name": "year",
        "byte_offset": 12,
        "data_type": "U2",
        "description": "Year, range 1999..2099 (UTC)",
        "unit": "y"
      },
      {
        "name": "month",
        "byte_offset": 14,
        "data_type": "U1",
        "description": "Month, range 1..12 (UTC)",
        "unit": "month"
      },
      {
        "name": "day",
        "byte_offset": 15,
        "data_type": "U1",
        "description": "Day of month, range 1..31 (UTC)",
        "unit": "d"
      },
      {
        "name": "hour",
        "byte_offset": 16,
        "data_type": "U1",
        "description": "Hour of day, range 0..23 (UTC)",
        "unit": "h"
      },
      {
        "name": "min",
        "byte_offset": 17,
        "data_type": "U1",
        "description": "Minute of hour, range 0..59 (UTC)",
        "unit": "min"
      },
      {
        "name": "sec",
        "byte_offset": 18,
        "data_type": "U1",
        "description": "Seconds of minute, range 0..60 (UTC)",
        "unit": "s"
      },
      {
        "name": "valid",
        "byte_offset": 19,
        "data_type": "X1",
        "description": "Validity Flags",
        "bitfield": {
          "bits": [
            {
              "name": "validTOW",
              "bit_start": 0,
              "bit_end": 0,
              "description": "1 = Valid Time of Week (see section Time validity in the integration manual for details)"
            },
            {
              "name": "validWKN",
              "bit_start": 1,
              "bit_end": 1,
              "description": "1 = Valid Week Number (see section Time validity in the integration manual for details)"
            },
            {
              "name": "validUTC",
              "bit_start": 2,
              "bit_end": 2,
              "description": "1 = Valid UTC Time"
            },
            {
              "name": "authStatus",
              "bit_start": 3,
              "bit_end": 3,
              "description": "Indicates if the parameters used to convert GNSS time into UTC time have been authenticated. 0 = Unknown, 1 = Authenticated"
            },
            {
              "name": "utcStandard",
              "bit_start": 4,
              "bit_end": 7,
              "description": "UTC standard identifier. (Not supported for protocol versions less than 15.00) 0 = Information not available, 1 = Communications Research Labratory (CRL), Tokyo, Japan, 2 = National Institute of Standards and Technology (NIST), 3 = U.S. Naval Observatory (USNO), 4 = International Bureau of Weights and Measures (BIPM), 5 = European laboratories, 6 = Former Soviet Union (SU), 7 = National Time Service Center (NTSC), China, 8 = National Physics Laboratory India (NPLI)"
            }
          ]
        }
      }
    ]
  }
}
