{
  "name": "UBX-RXM-COR",
  "class_id": "0x02",
  "message_id": "0x34",
  "description": "Differential correction input status",
  "message_type": "output",
  "payload": {
    "length": {
      "fixed": 12
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x01 for this version)"
      },
      {
        "name": "ebno",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Energy per bit to noise power spectral density ratio (Eb/NO). 0: unknown. Reported only for protocol UBX-RXM-PMP (SPARTN) to monitor signal quality.",
        "unit": "dB",
        "scale": {
          "raw": "2^-3",
          "multiplier": 0.125
        }
      },
      {
        "name": "reserved0",
        "byte_offset": 2,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "reserved1",
        "byte_offset": 3,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "statusInfo",
        "byte_offset": 4,
        "data_type": "X4",
        "description": "Message input status information",
        "bitfield": {
          "bits": [
            {
              "name": "protocol",
              "bit_start": 0,
              "bit_end": 4,
              "description": "Input correction data protocol: 0: Unknown, 1: RTCM3, 2: SPARTN (Secure Position Augmentation for Real Time Navigation), 29: UBX-RXM-PMP (SPARTN), 30: UBX-RXM-QZSSL6"
            },
            {
              "name": "errStatus",
              "bit_start": 5,
              "bit_end": 6,
              "description": "Error status of the received correction message content based on possibly available error codes or checksums: 0: Unknown, 1: Error-free, 2: Erroneous"
            },
            {
              "name": "msgUsed",
              "bit_start": 7,
              "bit_end": 8,
              "description": "Status of receiver using the input message: 0: Unknown, 1: Not used, 2: Used"
            },
            {
              "name": "correctionId",
              "bit_start": 9,
              "bit_end": 24,
              "description": "Identifier for the correction stream: For RTCM 3: Reference station ID (DF003) of the received RTCM input message. Valid range 0-4095. Reported only for the standard RTCM messages that include the DF003 field and for the u-blox proprietary RTCM messages 4072.x. For all other messages, reports 0xFFFF. For other correction protocols 0xFFFF."
            },
            {
              "name": "msgTypeValid",
              "bit_start": 25,
              "bit_end": 25,
              "description": "Validity of the msgType field. Set to False e.g. if the protocol does not define msgType."
            },
            {
              "name": "msgSubTypeValid",
              "bit_start": 26,
              "bit_end": 26,
              "description": "Validity of the msgSubType field. Set to False e.g. if the protocol does not define subtype for the msgType."
            },
            {
              "name": "msgInputHandle",
              "bit_start": 27,
              "bit_end": 27,
              "description": "Input handling support of the input message: 0: Receiver does not have input handling support for this message, 1: Receiver has input handling support for this message. Input handling support does not necessarily mean that message is supported/used by the receiver."
            },
            {
              "name": "msgEncrypted",
              "bit_start": 28,
              "bit_end": 29,
              "description": "Encryption status of the input message: 0: Unknown, 1: Not encrypted, 2: Encrypted"
            },
            {
              "name": "msgDecrypted",
              "bit_start": 30,
              "bit_end": 31,
              "description": "Decryption status of the input message: 0: Unknown, 1: Not decrypted, 2: Decrypted"
            }
          ]
        }
      },
      {
        "name": "msgType",
        "byte_offset": 8,
        "data_type": "U2",
        "description": "Message type"
      },
      {
        "name": "msgSubType",
        "byte_offset": 10,
        "data_type": "U2",
        "description": "Message subtype"
      }
    ]
  }
}
