  "class_id": "0x0B",
  "message_id": "0x02",
  "description": "GPS health, UTC and ionosphere parameters",
  "message_type": "output",
  "payload": {
    "length": {
      "fixed": 72
    },
    "fields": [
      {
        "name": "health",
        "byte_offset": 0,
        "data_type": "X4",
        "description": "Bitmask, every bit represents a GPS SV (1-32). If the bit is set the SV is healthy."
      },
      {
        "name": "utcA0",
        "byte_offset": 4,
        "data_type": "R8",
        "description": "UTC - parameter A0"
      },
      {
        "name": "utcA1",
        "byte_offset": 12,
        "data_type": "R8",
        "description": "UTC - parameter A1"
      },
      {
        "name": "utcTOW",
        "byte_offset": 20,
        "data_type": "I4",
        "description": "UTC - reference time of week"
      },
      {
        "name": "utcWNT",
        "byte_offset": 24,
        "data_type": "I2",
        "description": "UTC - reference week number"
      },
      {
        "name": "utcLS",
        "byte_offset": 26,
        "data_type": "I2",
        "description": "UTC - time difference due to leap seconds before event"
      },
      {
        "name": "utcWNF",
        "byte_offset": 28,
        "data_type": "I2",
        "description": "UTC - week number when next leap second event occurs"
      },
      {
        "name": "utcDN",
        "byte_offset": 30,
        "data_type": "I2",
        "description": "UTC - day of week when next leap second event occurs"
      },
      {
        "name": "utcLSF",
        "byte_offset": 32,
        "data_type": "I2",
        "description": "UTC - time difference due to leap seconds after event"
      },
      {
        "name": "utcSpare",
        "byte_offset": 34,
        "data_type": "I2",
        "description": "UTC - Spare to ensure structure is a multiple of 4 bytes"
      },
      {
        "name": "klobA0",
        "byte_offset": 36,
        "data_type": "R4",
        "description": "Klobuchar - alpha 0",
        "unit": "s"
      },
      {
        "name": "klobA1",
        "byte_offset": 40,
        "data_type": "R4",
        "description": "Klobuchar - alpha 1",
        "unit": "s/semi circle"
      },
      {
        "name": "klobA2",
        "byte_offset": 44,
        "data_type": "R4",
        "description": "Klobuchar - alpha 2",
        "unit": "s/semi circle^2"
      },
      {
        "name": "klobA3",
        "byte_offset": 48,
        "data_type": "R4",
        "description": "Klobuchar - alpha 3",
        "unit": "s/semi circle^3"
      },
      {
        "name": "klobB0",
        "byte_offset": 52,
        "data_type": "R4",
        "description": "Klobuchar - beta 0",
        "unit": "s"
      },
      {
        "name": "klobB1",
        "byte_offset": 56,
        "data_type": "R4",
        "description": "Klobuchar - beta 1",
        "unit": "s/semi circle"
      },
      {
        "name": "klobB2",
        "byte_offset": 60,
        "data_type": "R4",
        "description": "Klobuchar - beta 2",
        "unit": "s/semi circle^2"
      },
      {
        "name": "klobB3",
        "byte_offset": 64,
        "data_type": "R4",
        "description": "Klobuchar - beta 3",
        "unit": "s/semi circle^3"
      },
      {
        "name": "flags",
        "byte_offset": 68,
        "data_type": "X4",
        "description": "flags",
        "bitfield": {
          "bits": [
            {
              "name": "healthValid",
              "bit_start": 0,
              "bit_end": 0,
              "description": "Healthmask field in this message is valid"
            },
            {
              "name": "utcValid",
              "bit_start": 1,
              "bit_end": 1,
              "description": "UTC parameter fields in this message are valid"
            },
            {
              "name": "klobValid",
              "bit_start": 2,
              "bit_end": 2,
              "description": "Klobuchar parameter fields in this message are valid"
            }
          ]
        }
      }
    ]
  }
}

