  "description": "Get/set the Accelerometer (A) sensor configuration",
  "message_type": "get_set",
  "payload": {
    "length": {
      "fixed": 20
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
