        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "accelRmsThdl",
        "byte_offset": 10,
        "data_type": "U1",
        "description": "Accelerometer RMS threshold below which automatically estimated accelerometer noise-level (accuracy) is updated.",
        "unit": "m/s^2",
        "scale": {
          "raw": "2^-6",
          "multiplier": 0.015625
        }
      },
      {
        "name": "frequency",
        "byte_offset": 11,
        "data_type": "U1",
        "description": "Nominal accelerometer sensor data sampling frequency.",
        "unit": "Hz"
      },
      {
        "name": "latency",
        "byte_offset": 12,
        "data_type": "U2",
