{
  "name": "UBX-CFG-PM2",
  "class_id": "0x06",
  "message_id": "0x3B",
  "description": "Extended power management configuration",
  "message_type": "get_set",
  "payload": {
    "length": {
      "fixed": 48
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x02 for this version)"
      },
      {
        "name": "reserved1",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "maxStartupStateDur",
        "byte_offset": 2,
        "data_type": "U1",
        "description": "Maximum time to spend in Acquisition state. If 0: bound disabled (not supported in protocol versions 23 to 23.01).",
        "unit": "s"
      },
      {
        "name": "reserved2",
        "byte_offset": 3,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "flags",
        "byte_offset": 4,
        "data_type": "X4",
        "description": "PSM configuration flags",
        "bitfield": {
          "bits": [
            {
              "name": "optTarget",
              "bit_start": 1,
              "bit_end": 3,
              "description": "Optimization target (000 performance (default), 001 power save, 010-011, 100-111 reserved)"
            },
            {
              "name": "extintSel",
              "bit_start": 4,
              "bit_end": 4,
              "description": "EXTINT pin select (0=EXTINT0, 1=EXTINT1)"
            },
            {
              "name": "extintWake",
              "bit_start": 5,
              "bit_end": 5,
              "description": "EXTINT pin control (0=disabled, 1=enabled, keep receiver awake as long as selected EXTINT pin is 'high')"
            },
            {
              "name": "extintBackup",
              "bit_start": 6,
              "bit_end": 6,
              "description": "EXTINT Pin Control (0=disabled, 1=enabled, force receiver into BACKUP mode when selected EXTINT pin is 'low')"
            },
            {
              "name": "extintInactive",
              "bit_start": 7,
              "bit_end": 7,
              "description": "EXTINT Pin Control (0=disabled, 1=enabled, force backup in case EXTINT pin is inactive for time longer than extintIncactivityMs)"
            },
            {
              "name": "limitPeakCurr",
              "bit_start": 8,
              "bit_end": 9,
              "description": "Limit peak current (00=disabled, 01=enabled, peak current is limited, 10-11 reserved)"
            },
            {
              "name": "waitTimeFix",
              "bit_start": 10,
              "bit_end": 10,
              "description": "Wait for Timefix (0=wait for normal fix OK before starting on time, 1=wait for time fix OK before starting on time)"
            },
            {
              "name": "updateRTC",
              "bit_start": 11,
              "bit_end": 11,
              "description": "Update Real Time Clock (0=do not wake up to update RTC, 1=update RTC, receiver adds extra wake-up cycles)"
            },
            {
              "name": "updateEPH",
              "bit_start": 12,
              "bit_end": 12,
              "description": "Update Ephemeris (0=do not wake up to update Ephemeris data, 1=update Ephemeris, receiver adds extra wake-up cycles)"
            },
            {
              "name": "doNotEnterOff",
              "bit_start": 16,
              "bit_end": 16,
              "description": "Behavior of receiver in case of no fix (0=receiver enters Inactive, 1=receiver does not enter Inactive but keeps trying to acquire a fix instead)"
            },
            {
              "name": "mode",
              "bit_start": 17,
              "bit_end": 18,
              "description": "Mode of operation (00=ON/OFF operation (PSMOO), 01=cyclic tracking operation (PSMCT), 10-11 reserved)"
            }
          ]
        }
      },
      {
        "name": "updatePeriod",
        "byte_offset": 8,
        "data_type": "U4",
        "description": "Position update period. If set to 0, the receiver will never retry a fix and it will wait for external events.",
        "unit": "ms"
      },
      {
        "name": "searchPeriod",
        "byte_offset": 12,
        "data_type": "U4",
        "description": "Acquisition retry period if previously failed. If set to 0, the receiver will never retry a startup.",
        "unit": "ms"
      },
      {
        "name": "gridOffset",
        "byte_offset": 16,
        "data_type": "U4",
        "description": "Grid offset relative to GPS start of week",
        "unit": "ms"
      },
      {
        "name": "onTime",
        "byte_offset": 20,
        "data_type": "U2",
        "description": "Time to stay in Tracking state",
        "unit": "s"
      },
      {
        "name": "minAcqTime",
        "byte_offset": 22,
        "data_type": "U2",
        "description": "minimal search time",
        "unit": "s"
      },
      {
        "name": "reserved3",
        "byte_offset": 24,
        "data_type": {
          "array_of": "U1",
          "count": 20
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "extintInactivityMs",
        "byte_offset": 44,
        "data_type": "U4",
        "description": "inactivity time out on EXTINT pin if enabled",
        "unit": "ms"
      }
    ]
  }
}
