{
  "name": "UBX-CFG-PRT",
  "class_id": "0x06",
  "message_id": "0x00",
  "description": "Polls or configures the port settings for an I/O port (e.g., UART, USB, SPI). This definition describes the 20-byte Get/Set configuration.",
  "message_type": "get_set",
  "payload": {
    "length": {
      "fixed": 20
    },
    "fields": [
      {
        "name": "portID",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Port identifier number (see the other versions of CFG-PRT for valid values)"
      },
      {
        "name": "reserved1",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "txReady",
        "byte_offset": 2,
        "data_type": "X2",
        "description": "TX ready PIN configuration",
        "bitfield": {
          "bits": [
            {
              "name": "en",
              "bit_start": 0,
              "bit_end": 0,
              "description": "Enable TX ready feature for this port"
            },
            {
              "name": "pol",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Polarity (0 High-active, 1 Low-active)"
            },
            {
              "name": "pin",
              "bit_start": 2,
              "bit_end": 6,
              "description": "PIO to be used (must not be in use by another function)"
            },
            {
              "name": "thres",
              "bit_start": 7,
              "bit_end": 15,
              "description": "Threshold for TX ready PIN activation. The given threshold is multiplied by 8 bytes. Values like 0x000 (no threshold), 0x001 (8 bytes), 0x002 (16 bytes), ..., 0x1FE (4080 bytes), 0x1FF (4088 bytes)."
            }
          ]
        }
      },
      {
        "name": "mode",
        "byte_offset": 4,
        "data_type": "X4",
        "description": "A bit mask describing the UART mode",
        "bitfield": {
          "bits": [
            {
              "name": "charLen",
              "bit_start": 6,
              "bit_end": 7,
              "description": "Character length (00 5bit (not supported), 01 6bit (not supported), 10 7bit (supported only with parity), 11 8bit)"
            },
            {
              "name": "parity",
              "bit_start": 9,
              "bit_end": 11,
              "description": "Parity setting (000 Even parity, 001 Odd parity, 10X No parity, X1X Reserved)"
            },
            {
              "name": "nStopBits",
              "bit_start": 12,
              "bit_end": 13,
              "description": "Number of Stop bits (00 1 Stop bit, 01 1.5 Stop bit, 10 2 Stop bit, 11 0.5 Stop bit)"
            }
          ]
        }
      },
      {
        "name": "baudRate",
        "byte_offset": 8,
        "data_type": "U4",
        "description": "Baud rate in bits/second",
        "unit": "Bits/s"
      },
      {
        "name": "inProtoMask",
        "byte_offset": 12,
        "data_type": "X2",
        "description": "A mask describing which input protocols are active. Each bit of this mask is used for a protocol. Through that, multiple protocols can be defined on a single port.",
        "bitfield": {
          "bits": [
            {
              "name": "inUbx",
              "bit_start": 0,
              "bit_end": 0,
              "description": "UBX protocol"
            },
            {
              "name": "inNmea",
              "bit_start": 1,
              "bit_end": 1,
              "description": "NMEA protocol"
            },
            {
              "name": "inRtcm",
              "bit_start": 2,
              "bit_end": 2,
              "description": "RTCM2 protocol"
            },
            {
              "name": "inRtcm3",
              "bit_start": 5,
              "bit_end": 5,
              "description": "RTCM3 protocol (not supported in protocol versions less than 20)"
            }
          ]
        }
      },
      {
        "name": "outProtoMask",
        "byte_offset": 14,
        "data_type": "X2",
        "description": "A mask describing which output protocols are active. Each bit of this mask is used for a protocol. Through that, multiple protocols can be defined on a single port.",
        "bitfield": {
          "bits": [
            {
              "name": "outUbx",
              "bit_start": 0,
              "bit_end": 0,
              "description": "UBX protocol"
            },
            {
              "name": "outNmea",
              "bit_start": 1,
              "bit_end": 1,
              "description": "NMEA protocol"
            },
            {
              "name": "outRtcm3",
              "bit_start": 5,
              "bit_end": 5,
              "description": "RTCM3 protocol (not supported in protocol versions less than 20)"
            }
          ]
        }
      },
      {
        "name": "flags",
        "byte_offset": 16,
        "data_type": "X2",
        "description": "Flags bit mask",
        "bitfield": {
          "bits": [
            {
              "name": "extendedTxTimeout",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Extended TX timeout: if set, the port will time out if allocated TX memory >=4 kB and no activity for 1.5 s. If not set the port will time out if no activity for 1.5 s regardless on the amount of allocated TX memory."
            }
          ]
        }
      },
      {
        "name": "reserved2",
        "byte_offset": 18,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
