parts:
{
  "name": "UBX-CFG-RST",
  "class_id": "0x06",
  "message_id": "0x04",
  "description": "Reset receiver / Clear backup data structures",
  "message_type": "command",
  "payload": {
    "length": {
      "fixed": 4
    },
    "fields": [
      {
        "name": "navBbrMask",
        "byte_offset": 0,
        "data_type": "X2",
        "description": "BBR sections to clear. The following special sets apply: 0x0000 Hot start, 0x0001 Warm start, 0xFFFF Cold start.",
        "bitfield": {
          "bits": [
            {
              "name": "eph",
              "bit_start": 0,
              "bit_end": 0,
              "description": "Ephemeris"
            },
            {
              "name": "alm",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Almanac"
            },
            {
              "name": "health",
              "bit_start": 2,
              "bit_end": 2,
              "description": "Health"
            },
            {
              "name": "klob",
              "bit_start": 3,
              "bit_end": 3,
              "description": "Klobuchar parameters"
            },
            {
              "name": "pos",
              "bit_start": 4,
              "bit_end": 4,
              "description": "Position"
            },
            {
              "name": "clkd",
              "bit_start": 5,
              "bit_end": 5,
              "description": "Clock drift"
            },
            {
              "name": "OSC",
              "bit_start": 6,
              "bit_end": 6,
              "description": "Oscillator parameter"
            },
            {
              "name": "utc",
              "bit_start": 7,
              "bit_end": 7,
              "description": "UTC correction + GPS leap seconds parameters"
            },
            {
              "name": "rtc",
              "bit_start": 8,
              "bit_end": 8,
              "description": "RTC"
            },
            {
              "name": "sfdr",
              "bit_start": 9,
              "bit_end": 9,
              "description": "SFDR Parameters (only available on the ADR/UDR/HPS product variant) and weak signal compensation estimates"
            },
            {
              "name": "vmon",
              "bit_start": 10,
              "bit_end": 10,
              "description": "SFDR Vehicle Monitoring Parameter (only available on the ADR/UDR/HPS product variant)"
            },
            {
              "name": "tct",
              "bit_start": 11,
              "bit_end": 11,
              "description": "TCT Parameters (only available on the ADR/UDR/HPS product variant)"
            },
            {
              "name": "aop",
              "bit_start": 12,
              "bit_end": 12,
              "description": "Autonomous orbit parameters"
            }
          ]
        }
      },
      {
        "name": "resetMode",
        "byte_offset": 2,
        "data_type": "U1",
        "description": "Reset Type: 0x00 = Hardware reset (watchdog) immediately, 0x01 = Controlled software reset, 0x02 = Controlled software reset (GNSS only), 0x04 = Hardware reset (watchdog) after shutdown, 0x08 = Controlled GNSS stop, 0x09 = Controlled GNSS start"
      },
      {
        "name": "reserved1",
        "byte_offset": 3,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
