parts:
{
  "name": "UBX-ESF-MEAS",
  "class_id": "0x10",
  "message_id": "0x02",
  "description": "External sensor fusion measurements",
  "message_type": "input",
  "payload": {
    "length": {
      "variable": {
        "base": 8,
        "multiplier": 4,
        "min": 8,
        "formula_description": "8 (fixed header fields) + N (number of measurements) * 4 (bytes per data measurement). N is derived from the message length."
      }
    },
    "fields": [
      {
        "name": "timeTag",
        "byte_offset": 0,
        "data_type": "U4",
        "description": "Time tag of measurement generated by external sensor"
      },
      {
        "name": "flags",
        "byte_offset": 4,
        "data_type": "X2",
        "description": "Flags. Set all unused bits to zero.",
        "bitfield": {
          "bits": [
            {
              "name": "timeMarkSent",
              "bit_start": 0,
              "bit_end": 1,
              "description": "Time mark signal was supplied just prior to sending this message: 0 = none, 1 = on Ext0, 2 = on Ext1"
            },
            {
              "name": "timeMarkEdge",
              "bit_start": 2,
              "bit_end": 2,
              "description": "Trigger on rising (0) or falling (1) edge of time mark signal"
            },
            {
              "name": "calibTtagValid",
              "bit_start": 3,
              "bit_end": 3,
              "description": "Calibration time tag available. Always set to zero. (This implies the calibTtag field is never supplied as per its description)."
            },
            {
              "name": "reserved",
              "bit_start": 4,
              "bit_end": 15,
              "description": "Reserved"
            }
          ]
        }
      },
      {
        "name": "id",
        "byte_offset": 6,
        "data_type": "U2",
        "description": "Identification number of data provider"
      }
    ],
    "repeated_groups": [
      {
        "name": "measurements",
        "repetition_type": "count_by_remaining_length",
        "group_size_bytes": 4,
        "base_offset": 8,
        "fields": [
          {
            "name": "data",
            "byte_offset": 0,
            "data_type": "X4",
            "description": "Data",
            "bitfield": {
              "bits": [
                {
                  "name": "dataType",
                  "bit_start": 0,
                  "bit_end": 5,
                  "description": "Type of data (0 = no data; 1..63 = data type)"
                },
                {
                  "name": "dataField",
                  "bit_start": 6,
                  "bit_end": 29,
                  "description": "Data"
                },
                {
                  "name": "reserved",
                  "bit_start": 30,
                  "bit_end": 31,
                  "description": "Reserved"
                }
              ]
            }
          }
        ]
      }
    ]
  }
}
