{
  "name": "UBX-MON-SMGR",
  "class_id": "0x0A",
  "message_id": "0x2E",
  "description": "Synchronization manager status",
  "message_type": "output",
  "payload": {
    "length": {
      "fixed": 16
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)"
      },
      {
        "name": "reserved1",
        "byte_offset": 1,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "iTOW",
        "byte_offset": 4,
        "data_type": "U4",
        "description": "Time of the week",
        "unit": "ms"
      },
      {
        "name": "intosc",
        "byte_offset": 8,
        "data_type": "X2",
        "description": "A bit mask, indicating the status of the local oscillator (see graphic below)",
        "bitfield": {
          "bits": [
            {
              "name": "intOscState",
              "bit_start": 0,
              "bit_end": 1,
              "description": "State of the oscillator: 0: autonomous operation, 1: calibration ongoing, 2: oscillator is steered by the host, 3: idle state"
            },
            {
              "name": "intOscCalib",
              "bit_start": 2,
              "bit_end": 2,
              "description": "1 = oscillator gain is calibrated"
            },
            {
              "name": "intOscDisc",
              "bit_start": 3,
              "bit_end": 3,
              "description": "1 = signal is disciplined"
            },
            {
              "name": "reserved",
              "bit_start": 4,
              "bit_end": 15,
              "description": "Reserved",
              "reserved": true
            }
          ]
        }
      },
      {
        "name": "extosc",
        "byte_offset": 10,
        "data_type": "X2",
        "description": "A bit mask, indicating the status of the external oscillator (see graphic below)",
        "bitfield": {
          "bits": [
            {
              "name": "extOscState",
              "bit_start": 0,
              "bit_end": 1,
              "description": "State of the oscillator: 0: autonomous operation, 1: calibration ongoing, 2: oscillator is steered by the host, 3: idle state"
            },
            {
              "name": "extOscCalib",
              "bit_start": 2,
              "bit_end": 2,
              "description": "1 = oscillator gain is calibrated"
            },
            {
              "name": "extOscDisc",
              "bit_start": 3,
              "bit_end": 3,
              "description": "1 = signal is disciplined"
            },
            {
              "name": "reserved",
              "bit_start": 4,
              "bit_end": 15,
              "description": "Reserved",
              "reserved": true
            }
          ]
        }
      },
      {
        "name": "discSrc",
        "byte_offset": 12,
        "data_type": "U1",
        "description": "Disciplining source identifier: 0: internal oscillator, 1: GNSS, 2: EXTINT0, 3: EXTINT1, 4: internal oscillator measured by the host, 5: external oscillator measured by the host"
      },
      {
        "name": "gnss",
        "byte_offset": 13,
        "data_type": "X1",
        "description": "A bit mask, indicating the status of the GNSS (see graphic below)",
        "bitfield": {
          "bits": [
            {
              "name": "gnssAvail",
              "bit_start": 0,
              "bit_end": 0,
              "description": "1 = GNSS is present"
            },
            {
              "name": "reserved",
              "bit_start": 1,
              "bit_end": 7,
              "description": "Reserved",
              "reserved": true
            }
          ]
        }
      },
      {
        "name": "extInt0",
        "byte_offset": 14,
        "data_type": "X1",
        "description": "A bit mask, indicating the status of the external input 0 (see graphic below)",
        "bitfield": {
          "bits": [
            {
              "name": "extInt0Avail",
              "bit_start": 0,
              "bit_end": 0,
              "description": "1 = signal present at this input"
            },
            {
              "name": "extInt0Type",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Source type: 0: frequency, 1: time"
            },
            {
              "name": "extInt0FeedBack",
              "bit_start": 2,
              "bit_end": 2,
              "description": "This source is used as feedback of the external oscillator"
            },
            {
              "name": "reserved",
              "bit_start": 3,
              "bit_end": 7,
              "description": "Reserved",
              "reserved": true
            }
          ]
        }
      },
      {
        "name": "extInt1",
        "byte_offset": 15,
        "data_type": "X1",
        "description": "A bit mask, indicating the status of the external input 1 (see graphic below)",
        "bitfield": {
          "bits": [
            {
              "name": "extInt1Avail",
              "bit_start": 0,
              "bit_end": 0,
              "description": "1 = signal present at this input"
            },
            {
              "name": "extInt1Type",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Source type: 0: frequency, 1: time"
            },
            {
              "name": "extInt1FeedBack",
              "bit_start": 2,
              "bit_end": 2,
              "description": "This source is used as feedback of the external oscillator"
            },
            {
              "name": "reserved",
              "bit_start": 3,
              "bit_end": 7,
              "description": "Reserved",
              "reserved": true
            }
          ]
        }
      }
    ]
  }
}

