            },
            "unit": "Hz",
            "description": "Doppler frequency with respect to 1575.4282MHz [IIIII.FFF Hz]"
          },
          {
            "name": "position1_1",
            "byte_offset": 16,
            "data_type": "X4",
            "description": "Position 1 Frame (part 1/2)",
            "bitfield": {
              "bits": [
                {
                  "name": "pos1Floor",
                  "bit_start": 0,
                  "bit_end": 7,
                  "data_type": "signed",
                  "description": "Floor number [1.0 floor resolution] (Offset: -50 floor)"
                },
                {
                  "name": "pos1Lat",
                  "bit_start": 8,
                  "bit_end": 30,
                  "data_type": "signed",
                  "description": "Latitude [deg* (180/2^23)]"
                },
                {
                  "name": "reserved",
                  "bit_start": 31,
                  "bit_end": 31,
                  "data_type": "unsigned",
                  "description": "Reserved",
                  "reserved": true
                }
              ]
            }
