                {
                  "name": "pos2Valid",
                  "bit_start": 18,
                  "bit_end": 18,
                  "data_type": "unsigned",
                  "description": "Position 2 Frame valid"
                },
                {
                  "name": "reserved",
                  "bit_start": 19,
                  "bit_end": 31,
                  "data_type": "unsigned",
