          },
          {
            "name": "mediumId_2",
            "byte_offset": 44,
            "data_type": "X4",
            "description": "Medium ID Frame (part 2/2)",
            "bitfield": {
              "bits": [
                {
                  "name": "mediumIdMSB",
                  "bit_start": 0,
                  "bit_end": 1,
                  "data_type": "unsigned",
                  "description": "Medium ID MSB"
                },
                {
                  "name": "mediumValid",
                  "bit_start": 2,
                  "bit_end": 2,
                  "data_type": "unsigned",
                  "description": "Medium ID Frame valid"
                },
                {
                  "name": "mediumBoundary",
                  "bit_start": 3,
                  "bit_end": 3,
                  "data_type": "unsigned",
                  "description": "Boundary Bit"
                },
                {
                  "name": "reserved",
                  "bit_start": 4,
                  "bit_end": 31,
                  "data_type": "unsigned",
                  "description": "Reserved",
