{
  "name": "UBX-RXM-SVSI",
  "class_id": "0x02",
  "message_id": "0x20",
  "description": "SV status info",
  "message_type": "output",
  "payload": {
    "length": {
      "variable": {
        "base": 8,
        "formula": "8 + 6*numSV",
        "min": 8
      }
    },
    "fields": [
      {
        "name": "iTow",
        "byte_offset": 0,
        "data_type": "U4",
        "description": "GPS time of week of the navigation epoch. See the description of iTOW for details.",
        "unit": "ms"
      },
      {
        "name": "week",
        "byte_offset": 4,
        "data_type": "I2",
        "description": "GPS week number of the navigation epoch",
        "unit": "weeks"
      },
      {
        "name": "numVis",
        "byte_offset": 6,
        "data_type": "U1",
        "description": "Number of visible satellites"
      },
      {
        "name": "numSV",
        "byte_offset": 7,
        "data_type": "U1",
        "description": "Number of per-SV data blocks following"
      }
    ],
    "repeated_groups": [
      {
        "name": "svData",
        "repetition_type": "count_field",
        "count_field": "numSV",
        "group_size_bytes": 6,
        "base_offset": 8,
        "fields": [
          {
            "name": "svid",
            "byte_offset": 0,
            "data_type": "U1",
            "description": "Satellite ID"
          },
          {
            "name": "svFlag",
            "byte_offset": 1,
            "data_type": "X1",
            "description": "Information Flags",
            "bitfield": {
              "bits": [
                {
                  "name": "ura",
                  "bit_start": 0,
                  "bit_end": 3,
                  "description": "Figure of Merit (URA) range 0..15"
                },
                {
                  "name": "healthy",
                  "bit_start": 4,
                  "bit_end": 4,
                  "description": "SV healthy flag"
                },
                {
                  "name": "ephVal",
                  "bit_start": 5,
                  "bit_end": 5,
                  "description": "Ephemeris valid"
                },
                {
                  "name": "almVal",
                  "bit_start": 6,
                  "bit_end": 6,
                  "description": "Almanac valid"
                },
                {
                  "name": "notAvail",
                  "bit_start": 7,
                  "bit_end": 7,
                  "description": "SV not available"
                }
              ]
            }
          },
          {
            "name": "azim",
            "byte_offset": 2,
            "data_type": "I2",
            "description": "Azimuth"
          },
          {
            "name": "elev",
            "byte_offset": 4,
            "data_type": "I1",
            "description": "Elevation"
          },
          {
            "name": "age",
            "byte_offset": 5,
            "data_type": "X1",
            "description": "Age of Almanac and Ephemeris",
            "bitfield": {
              "bits": [
                {
                  "name": "almAge",
                  "bit_start": 0,
                  "bit_end": 3,
                  "description": "Age of ALM in days offset by 4. i.e. the reference time may be in the future: ageOfAlm = (age & 0x0f) - 4"
                },
                {
                  "name": "ephAge",
                  "bit_start": 4,
                  "bit_end": 7,
                  "description": "Age of EPH in hours offset by 4. i.e. the reference time may be in the future: ageOfEph = ((age & 0xf0) >> 4) - 4"
                }
              ]
            }
          }
        ]
      }
    ]
  }
}

