parts:
[
  {
    "name": "UBX-AID-INI",
    "description": "Aiding position, time, frequency, clock drift",
    "message_class": "0x0B",
    "message_id": "0x01",
    "message_type": "input_output",
    "payload_length": {
      "fixed": 48
    },
    "fields": [
      {
        "byte_offset": 0,
        "name": "ecefXOrLat",
        "data_type": "I4",
        "unit": "cm",
        "description": "WGS84 ECEF X coordinate or latitude, depending on flags below (value is degrees * 1e-7 when flag 'lla' is set)"
      },
      {
        "byte_offset": 4,
        "name": "ecefYOrLon",
        "data_type": "I4",
        "unit": "cm",
        "description": "WGS84 ECEF Y coordinate or longitude, depending on flags below (value is degrees * 1e-7 when flag 'lla' is set)"
      },
      {
        "byte_offset": 8,
        "name": "ecefZOrAlt",
        "data_type": "I4",
        "unit": "cm",
        "description": "WGS84 ECEF Z coordinate or altitude, depending on flags below"
      },
      {
        "byte_offset": 12,
        "name": "posAcc",
        "data_type": "U4",
        "unit": "cm",
        "description": "Position accuracy (stddev)"
      },
      {
        "byte_offset": 16,
        "name": "tmCfg",
        "data_type": "X2",
        "description": "Time mark configuration",
        "bitfield_members": [
          {
            "name": "fEdge",
            "bit_offset": 0,
            "size_bits": 1,
            "description": "use falling edge (default rising)"
          },
          {
            "name": "f1",
            "bit_offset": 1,
            "size_bits": 1,
            "description": "frequency on extint 1 (default extint 0)"
          },
          {
            "bit_offset": 2,
            "size_bits": 2,
            "reserved": true
          },
          {
            "name": "tm1",
            "bit_offset": 4,
            "size_bits": 1,
            "description": "time mark on extint 1 (default extint 0)"
          },
          {
            "bit_offset": 5,
            "size_bits": 11,
            "reserved": true
          }
        ]
      },
      {
        "byte_offset": 18,
        "name": "wnoOrDate",
        "data_type": "U2",
        "unit": "week",
        "description": "Actual week number or yearSince2000/Month (YYMM), depending on flags below"
      },
      {
        "byte_offset": 20,
        "name": "towOrTime",
        "data_type": "U4",
        "unit": "ms",
        "description": "Actual time of week or DayOfMonth/Hour/Minute/Second (DDHHMMSS), depending on flags below"
      },
      {
        "byte_offset": 24,
        "name": "towNs",
        "data_type": "I4",
        "unit": "ns",
        "description": "Fractional part of time of week"
      },
      {
        "byte_offset": 28,
        "name": "tAccMs",
        "data_type": "U4",
        "unit": "ms",
        "description": "Milliseconds part of time accuracy"
      },
      {
        "byte_offset": 32,
        "name": "tAccNs",
        "data_type": "U4",
        "unit": "ns",
        "description": "Nanoseconds part of time accuracy"
      },
      {
        "byte_offset": 36,
        "name": "clkDOrFreq",
        "data_type": "I4",
        "unit": "ns/s",
        "description": "Clock drift or frequency, depending on flags below (value is Hz * 1e-2 when flag 'clockF' is set)"
      },
      {
        "byte_offset": 40,
        "name": "clkDAccOrFreqAcc",
        "data_type": "U4",
        "unit": "ns/s",
        "description": "Accuracy of clock drift or frequency, depending on flags below (value is ppb when flag 'clockF' is set)"
      },
      {
        "byte_offset": 44,
        "name": "flags",
        "data_type": "X4",
        "description": "Bitmask with various configuration flags",
        "bitfield_members": [
          {
            "name": "pos",
            "bit_offset": 0,
            "size_bits": 1,
            "description": "Position is valid"
          },
          {
            "name": "time",
            "bit_offset": 1,
            "size_bits": 1,
            "description": "Time is valid"
          },
          {
            "name": "clockD",
            "bit_offset": 2,
            "size_bits": 1,
            "description": "Clock drift data contains valid clock drift, must not be set together with clockF"
          },
          {
            "name": "tp",
            "bit_offset": 3,
            "size_bits": 1,
            "description": "Use time pulse"
          },
          {
            "name": "clockF",
            "bit_offset": 4,
            "size_bits": 1,
            "description": "Clock drift data contains valid frequency, must not be set together with clockD"
          },
          {
            "name": "lla",
            "bit_offset": 5,
            "size_bits": 1,
            "description": "Position is given in lat/long/alt (default is ECEF)"
          },
          {
            "name": "altInv",
            "bit_offset": 6,
            "size_bits": 1,
            "description": "Altitude is not valid, if lla was set"
          },
          {
            "name": "prevTm",
            "bit_offset": 7,
            "size_bits": 1,
            "description": "Use time mark received before AID-INI message (default uses mark received after message)"
          },
          {
            "bit_offset": 8,
            "size_bits": 2,
            "reserved": true
          },
          {
            "name": "utc",
            "bit_offset": 10,
            "size_bits": 1,
            "description": "Time is given as UTC date/time (default is GPS wno/tow)"
          },
          {
            "bit_offset": 11,
            "size_bits": 21,
            "reserved": true
          }
        ]
      }
    ]
  }
]
