parts:
{
  "name": "UBX-CFG-ESFA",
  "class_id": "0x06",
  "message_id": "0x4C",
  "description": "Get/set the Accelerometer (A) sensor configuration",
  "message_type": "get_set",
  "payload_length": {
    "fixed": 20
  },
  "payload_fields": [
    {
      "byte_offset": 0,
      "data_type": "U1",
      "name": "version",
      "description": "Message version (0x00 for this version)",
      "fixed_value": 0
    },
    {
      "byte_offset": 1,
      "data_type": {
        "array_of": "U1",
        "count": 9
      },
      "name": "reserved1",
      "description": "Reserved",
      "reserved": true
    },
    {
      "byte_offset": 10,
      "data_type": "U1",
      "name": "accelRmsThdl",
      "scale": {
        "raw": "2^-6",
        "multiplier": 0.015625
      },
      "unit": "m/s^2",
      "description": "Accelerometer RMS threshold below which automatically estimated accelerometer noise-level (accuracy) is updated."
    },
    {
      "byte_offset": 11,
      "data_type": "U1",
      "name": "frequency",
      "unit": "Hz",
      "description": "Nominal accelerometer sensor data sampling frequency."
    },
    {
      "byte_offset": 12,
      "data_type": "U2",
      "name": "latency",
      "unit": "ms",
      "description": "Accelerometer sensor data latency due to e.g. CAN bus."
    },
    {
      "byte_offset": 14,
      "data_type": "U2",
      "name": "accuracy",
      "scale": {
        "raw": "1e-4",
        "multiplier": 0.0001
      },
      "unit": "m/s^2",
      "description": "Accelerometer sensor data accuracy."
    },
    {
      "byte_offset": 16,
      "data_type": {
        "array_of": "U1",
        "count": 4
      },
      "name": "reserved2",
      "description": "Reserved",
      "reserved": true
    }
  ]
}
