    "description": "Get/set the Gyroscope (G) sensor configuration",
    "payload_length": {
      "fixed": 20
    },
    "fields": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "name": "version",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "byte_offset": 1,
        "data_type": {
          "array_of": "U1",
