parts:
{
  "name": "UBX-CFG-ESRC",
  "class_id": "0x06",
  "message_id": "0x60",
  "description": "External synchronization source configuration",
  "message_type": "get_set",
  "payload_length": {
    "variable": {
      "base": 4,
      "formula": "4 + numSources*36"
    }
  },
  "fields": [
    {
      "byte_offset": 0,
      "name": "version",
      "data_type": "U1",
      "description": "Message version (0x00 for this version)",
      "fixed_value": 0
    },
    {
      "byte_offset": 1,
      "name": "numSources",
      "data_type": "U1",
      "description": "Number of sources (affects length of this message)"
    },
    {
      "byte_offset": 2,
      "name": "reserved1",
      "data_type": {
        "array_of": "U1",
        "count": 2
      },
      "description": "Reserved",
      "reserved": true
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          4
        ]
      },
      "name": "extInt",
      "data_type": "U1",
      "description": "EXTINT index of this source (0 for EXTINT0 and 1 for EXTINT1)",
      "is_repeated": true,
      "num_repeats_field": "numSources"
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          5
        ]
      },
      "name": "sourceType",
      "data_type": "U1",
      "description": "Source type: 0: none\\n1: frequency source; use with Temp, withAge, timeToTemp and maxDevLifeTime to describe the stability of the source\\n2: time source; use offset, offsetUncertainty and jitter fields to describe the stability of the source\\n3: feedback from external oscillator; stability data is taken from the external oscillator's configuration",
      "is_repeated": true,
      "num_repeats_field": "numSources"
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          6
        ]
      },
      "name": "flags",
      "data_type": "X2",
      "description": "Flags",
      "is_repeated": true,
      "num_repeats_field": "numSources",
      "sub_fields": [
        {
          "name": "polarity",
          "data_type": "U1",
          "bit_offset": 0,
          "num_bits": 1,
          "description": "Polarity of signal: 0: leading edge is rising edge, 1: leading edge is falling edge"
        },
        {
          "name": "gnssUtc",
          "data_type": "U1",
          "bit_offset": 1,
          "num_bits": 1,
          "description": "Time base of timing signal: 0: GNSS - as specified in CFG-TP5 (or GPS if CFG-TP5 indicates UTC), 1: UTC.\\nOnly used if sourceType is 2."
        }
      ]
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          8
        ]
      },
      "name": "freq",
      "data_type": "U4",
      "scale": {
        "multiplier": 0.25,
        "raw_string": "2^-2"
      },
      "unit": "Hz",
      "description": "Nominal frequency of source",
      "is_repeated": true,
      "num_repeats_field": "numSources"
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          12
        ]
      },
      "name": "reserved2",
      "data_type": {
        "array_of": "U1",
        "count": 4
      },
      "description": "Reserved",
      "reserved": true,
      "is_repeated": true,
      "num_repeats_field": "numSources"
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          16
        ]
      },
      "name": "withTemp",
      "data_type": "U4",
      "scale": {
        "multiplier": 0.00390625,
        "raw_string": "2^-8"
      },
      "unit": "ppb",
      "description": "Oscillator stability limit over operating temperature range (must be > 0)\\nOnly used if sourceType is 1.",
      "is_repeated": true,
      "num_repeats_field": "numSources"
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          20
        ]
      },
      "name": "withAge",
      "data_type": "U4",
      "scale": {
        "multiplier": 0.00390625,
        "raw_string": "2^-8"
      },
      "unit": "ppb/year",
      "description": "Oscillator stability with age (must be > 0)\\nOnly used if sourceType is 1.",
      "is_repeated": true,
      "num_repeats_field": "numSources"
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          24
        ]
      },
      "name": "timeToTemp",
      "data_type": "U2",
      "unit": "s",
      "description": "The minimum time that it could take for a temperature variation to move the oscillator frequency by 'withTemp' (must be > 0)\\nOnly used if sourceType is 1.",
      "is_repeated": true,
      "num_repeats_field": "numSources"
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          26
        ]
      },
      "name": "maxDevLifeTime",
      "data_type": "U2",
      "unit": "ppb",
      "description": "Maximum frequency deviation during lifetime (must be > 0)\\nOnly used if sourceType is 1.",
      "is_repeated": true,
      "num_repeats_field": "numSources"
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          28
        ]
      },
      "name": "offset",
      "data_type": "I4",
      "unit": "ns",
      "description": "Phase offset of signal\\nOnly used if sourceType is 2.",
      "is_repeated": true,
      "num_repeats_field": "numSources"
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          32
        ]
      },
      "name": "offsetUncertainty",
      "data_type": "U4",
      "unit": "ns",
      "description": "Uncertainty of phase offset (one standard deviation)\\nOnly used if sourceType is 2.",
      "is_repeated": true,
      "num_repeats_field": "numSources"
    },
    {
      "byte_offset": {
        "add": [
          {
            "field": "N",
            "multiplier": 36
          },
          36
        ]
      },
      "name": "jitter",
      "data_type": "U4",
      "unit": "ns/s",
      "description": "Phase jitter (must be > 0)\\nOnly used if sourceType is 2.",
      "is_repeated": true,
      "num_repeats_field": "numSources"
    }
  ]
}
