{
  "ubx_class": "CFG",
  "message_id": "INF",
  "class_id": "0x06",
  "message_id_val": "0x02",
  "name": "UBX-CFG-INF",
  "description": "Information message configuration",
  "message_type": "get_set",
  "payload_length": {
    "variable": {
      "base": 0,
      "formula": "10 * N"
    }
  },
  "payload_fields": [
    {
      "byte_offset": "N*10",
      "data_type": "U1",
      "name": "protocolID",
      "description": "Protocol identifier, identifying for which protocol the configuration is set/get. The following are valid protocol identifiers: 0: UBX protocol, 1: NMEA protocol, 2-255: Reserved"
    },
    {
      "byte_offset": "1 + N*10",
      "data_type": {
        "array_of": "U1",
        "count": 3
      },
      "name": "reserved1",
      "reserved": true
    },
    {
      "byte_offset": "4 + N*10",
      "data_type": {
        "array_of": "X1",
        "count": 6
      },
      "name": "infMsgMask",
      "description": "A bit mask, saying which information messages are enabled on each I/O port (see graphic below)",
      "bitfield": [
        {
          "name": "ERROR",
          "description": "enable ERROR",
          "position": 0,
          "size": 1
        },
        {
          "name": "WARNING",
          "description": "enable WARNING",
          "position": 1,
          "size": 1
        },
        {
          "name": "NOTICE",
          "description": "enable NOTICE",
          "position": 2,
          "size": 1
        },
        {
          "name": "TEST",
          "description": "enable TEST",
          "position": 3,
          "size": 1
        },
        {
          "name": "DEBUG",
          "description": "enable DEBUG",
          "position": 4,
          "size": 1
        }
      ]
    }
  ]
}
