        "offset": 3,
        "name": "flags",
        "type": "X1",
        "description": "flags",
        "bitfield": [
          {
            "name": "compat",
            "position": 0,
            "description": "enable compatibility mode."
          },
          {
            "name": "consider",
            "position": 1,
            "description": "enable considering mode."
          }
        ]
      },
      {
        "offset": 4,
        "name": "gnssToFilter",
        "type": "X4",
        "description": "Filters out satellites based on their GNSS. If a bitfield is enabled, the corresponding satellites will be not output.",
        "bitfield": [
          {
            "name": "gps",
            "position": 0,
            "description": "Disable reporting of GPS satellites"
          },
          {
            "name": "sbas",
            "position": 1,
            "description": "Disable reporting of SBAS satellites"
          },
          {
            "name": "galileo",
            "position": 2,
            "description": "Disable reporting of Galileo satellites"
          },
          {
            "name": "qzss",
            "position": 3,
            "description": "Disable reporting of QZSS satellites"
          },
          {
            "name": "glonass",
            "position": 5,
            "description": "Disable reporting of GLONASS satellites"
          },
          {
            "name": "beidou",
            "position": 6,
            "description": "Disable reporting of BeiDou satellites"
          }
        ]
      },
      {
        "offset": 8,
        "name": "svNumbering",
        "type": "U1",
        "description": "Configures the display of satellites that do not have an NMEA-defined value.",
        "values": [
          {
            "value": 0,
            "description": "Strict - Satellites are not output"
          },
          {
            "value": 1,
            "description": "Extended - Use proprietary numbering (see Satellite Numbering)"
          }
        ]
      },
      {
        "offset": 9,
        "name": "mainTalkerId",
        "type": "CH",
        "description": "Main Talker ID. 0: Main Talker ID is not overridden, 1: Set main Talker ID to 'GP', 2: Set main Talker ID to 'GL', 3: Set main Talker ID to 'GN', 4: Set main Talker ID to 'GA', 5: Set main Talker ID to 'GB', 6: Set main Talker ID to 'GQ' (available in NMEA 4.11 and later).",
        "values": [
          {
            "value": 0,
            "description": "Main Talker ID is not overridden"
          },
          {
            "value": 1,
            "description": "Set main Talker ID to 'GP'"
          },
          {
            "value": 2,
            "description": "Set main Talker ID to 'GL'"
          },
          {
            "value": 3,
            "description": "Set main Talker ID to 'GN'"
          },
          {
            "value": 4,
            "description": "Set main Talker ID to 'GA'"
          },
          {
            "value": 5,
            "description": "Set main Talker ID to 'GB'"
          },
          {
            "value": 6,
            "description": "Set main Talker ID to 'GQ' (available in NMEA 4.11 and later)"
          }
        ]
      },
      {
        "offset": 10,
        "name": "gsvTalkerId",
        "type": "CH",
        "description": "GSV Talker ID. 0: Use GNSS-specific Talker ID (as defined by NMEA), 1: Use the main Talker ID.",
        "values": [
          {
            "value": 0,
            "description": "Use GNSS-specific Talker ID (as defined by NMEA)"
          },
          {
            "value": 1,
            "description": "Use the main Talker ID"
          }
        ]
      },
      {
        "offset": 11,
        "name": "version",
        "type": "U1",
        "description": "Message version (0x00 for this version)",
        "values": [
          {
            "value": "0x00",
            "description": "This version"
          }
        ]
      }
    ]
  },
