parts:
[
  {
    "ubx_class": "0x06",
    "ubx_id": "0x3B",
    "name": "UBX-CFG-PM2",
    "message_type": "get_set",
    "description": "Extended power management configuration",
    "supported_firmware": "u-blox 8 / u-blox M8 protocol versions 18, 19, 19.1, 19.2, 20, 20.01, 20.1, 20.2, 20.3, 22, 23.01",
    "comment": "This feature is not supported for either the ADR or FTS products.",
    "payload_length": {
      "fixed": 48
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "name": "version",
        "description": "Message version (0x02 for this version). Note: the message version number is the same as for protocol version 23.01; please select correct message version based on the protocol version supported by your firmware.",
        "fixed_value": 2
      },
      {
        "byte_offset": 1,
        "data_type": "U1",
        "name": "reserved1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 2,
        "data_type": "U1",
        "name": "maxStartupStateDur",
        "unit": "s",
        "description": "Maximum time to spend in Acquisition state. If 0: bound disabled. (see maxStartupStateDur) (not supported in protocol versions 23 to 23.01)."
      },
      {
        "byte_offset": 3,
        "data_type": "U1",
        "name": "reserved2",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 4,
        "data_type": "X4",
        "name": "flags",
        "description": "PSM configuration flags",
        "bitfield": [
          {
            "bit_offset": 0,
            "num_bits": 3,
            "name": "optTarget",
            "description": "Optimization target",
            "values": [
              {
                "value": 0,
                "description": "performance (default)"
              },
              {
                "value": 1,
                "description": "power save"
              },
              {
                "value": 2,
                "description": "reserved"
              },
              {
                "value": 3,
                "description": "reserved"
              },
              {
                "value": 4,
                "description": "reserved"
              },
              {
                "value": 5,
                "description": "reserved"
              },
              {
                "value": 6,
                "description": "reserved"
              },
              {
                "value": 7,
                "description": "reserved"
              }
            ]
          },
          {
            "bit_offset": 3,
            "num_bits": 2,
            "name": "extintSel",
            "description": "EXTINT pin select",
            "values": [
              {
                "value": 0,
                "description": "EXTINT0"
              },
              {
                "value": 1,
                "description": "EXTINT1"
              }
            ]
          },
          {
            "bit_offset": 5,
            "num_bits": 2,
            "name": "extintWake",
            "description": "EXTINT pin control",
            "values": [
              {
                "value": 0,
                "description": "disabled"
              },
              {
                "value": 1,
                "description": "enabled, keep receiver awake as long as selected EXTINT pin is 'high'"
              }
            ]
          },
          {
            "bit_offset": 7,
            "num_bits": 1,
            "name": "extintBackup",
            "description": "EXTINT pin control",
            "values": [
              {
                "value": 0,
                "description": "disabled"
              },
              {
                "value": 1,
                "description": "enabled, force receiver into BACKUP mode when selected EXTINT pin is 'low'"
              }
            ]
          },
          {
            "bit_offset": 8,
            "num_bits": 2,
            "name": "limitPeakCurr",
            "description": "Limit peak current",
            "values": [
              {
                "value": 0,
                "description": "disabled"
              },
              {
                "value": 1,
                "description": "enabled, peak current is limited"
              },
              {
                "value": 2,
                "description": "reserved"
              },
              {
                "value": 3,
                "description": "reserved"
              }
            ]
          },
          {
            "bit_offset": 10,
            "num_bits": 1,
            "name": "waitTimeFix",
            "description": "Wait for Timefix",
            "values": [
              {
                "value": 0,
                "description": "wait for normal fix OK before starting on time"
              },
              {
                "value": 1,
                "description": "wait for time fix OK before starting on time"
              }
            ]
          },
          {
            "bit_offset": 11,
            "num_bits": 1,
            "name": "updateRTC",
            "description": "Update Real Time Clock",
            "values": [
              {
                "value": 0,
                "description": "do not wake up to update RTC. RTC is updated during normal on-time."
              },
              {
                "value": 1,
                "description": "update RTC. The receiver adds extra wake-up cycles to update the RTC."
              }
            ]
          },
          {
            "bit_offset": 12,
            "num_bits": 1,
            "name": "updateEPH",
            "description": "Update Ephemeris",
            "values": [
              {
                "value": 0,
                "description": "do not wake up to update Ephemeris data"
              },
              {
                "value": 1,
                "description": "update Ephemeris. The receiver adds extra wake-up cycles to update the Ephemeris data"
              }
            ]
          },
          {
            "bit_offset": 13,
            "num_bits": 3,
            "name": "reserved_13_15",
            "description": "Reserved",
            "reserved": true
          },
          {
            "bit_offset": 16,
            "num_bits": 2,
            "name": "mode",
            "description": "Mode of operation",
            "values": [
              {
                "value": 0,
                "description": "ON/OFF operation (PSMOO)"
              },
              {
                "value": 1,
                "description": "cyclic tracking operation (PSMCT)"
              },
              {
                "value": 2,
                "description": "reserved"
              },
              {
                "value": 3,
                "description": "reserved"
              }
            ]
          },
          {
            "bit_offset": 18,
            "num_bits": 1,
            "name": "doNotEnterOff",
            "description": "Behavior of receiver in case of no fix",
            "values": [
              {
                "value": 0,
                "description": "receiver enters (Inactive) Awaiting next search state"
              },
              {
                "value": 1,
                "description": "receiver does not enter (Inactive) Awaiting next search state but keeps trying to acquire a fix instead"
              }
            ]
          },
          {
            "bit_offset": 19,
            "num_bits": 13,
            "name": "reserved_19_31",
            "description": "Reserved",
            "reserved": true
          }
        ]
      },
      {
        "byte_offset": 8,
        "data_type": "U4",
        "name": "updatePeriod",
        "unit": "ms",
        "description": "Position update period. If set to 0, the receiver will never retry a fix and it will wait for external events."
      },
      {
        "byte_offset": 12,
        "data_type": "U4",
        "name": "searchPeriod",
        "unit": "ms",
        "description": "Acquisition retry period if previously failed. If set to 0, the receiver will never retry a startup. (not supported in protocol versions 23 to 23.01)."
      },
      {
        "byte_offset": 16,
        "data_type": "U4",
        "name": "gridOffset",
        "unit": "ms",
        "description": "Grid offset relative to GPS start of week (not supported in protocol versions 23 to 23.01)."
      },
      {
        "byte_offset": 20,
        "data_type": "U2",
        "name": "onTime",
        "unit": "s",
        "description": "Time to stay in Tracking state (not supported in protocol versions 23 to 23.01)."
      },
      {
        "byte_offset": 22,
        "data_type": "U2",
        "name": "minAcqTime",
        "unit": "s",
        "description": "minimal search time"
      },
      {
        "byte_offset": 24,
        "data_type": {
          "array_of": "U1",
          "count": 20
        },
        "name": "reserved3",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 44,
        "data_type": "U4",
        "name": "extintInactivityMs",
        "unit": "ms",
        "description": "inactivity time out on EXTINT pin if enabled"
      }
    ]
  }
]
