{
  "ubx_message": {
    "name": "UBX-CFG-PMS",
    "class_id": "0x06",
    "message_id": "0x86",
    "description": "Power mode setup",
    "message_type": "get_set",
    "payload_length": {
      "fixed": 8
    },
    "payload_structure": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "name": "version",
        "description": "Message version",
        "fixed_value": 0
      },
      {
        "byte_offset": 1,
        "data_type": "U1",
        "name": "powerSetupValue",
        "description": "Power setup value",
        "values": [
          {
            "value": 0,
            "description": "Full power"
          },
          {
            "value": 1,
            "description": "Balanced"
          },
          {
            "value": 2,
            "description": "Interval"
          },
          {
            "value": 3,
            "description": "Aggressive with 1 Hz"
          },
          {
            "value": 4,
            "description": "Aggressive with 2 Hz"
          },
          {
            "value": 5,
            "description": "Aggressive with 4 Hz"
          },
          {
            "value": 255,
            "description": "Invalid (only when polling)"
          }
        ]
      },
      {
        "byte_offset": 2,
        "data_type": "U2",
        "name": "period",
        "unit": "s",
        "description": "Position update period and search period. Recommended minimum period is 10 s, although the receiver accepts any value bigger than 5 s. Only valid when powerSetupValue set to Interval, otherwise must be set to 'O'."
      },
      {
        "byte_offset": 4,
        "data_type": "U2",
        "name": "onTime",
        "unit": "s",
        "description": "Duration of the ON phase, must be smaller than the period. Only valid when powerSetupValue set to Interval, otherwise must be set to 'O'."
      },
      {
        "byte_offset": 6,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "name": "reserved1",
        "reserved": true
      }
    ]
  }
}
