parts:
{
  "ubx_message": {
    "name": "UBX-CFG-RST",
    "class_id": "0x06",
    "message_id": "0x04",
    "description": "Reset receiver / Clear backup data structures",
    "message_type": "command",
    "payload_length": {
      "fixed": 4
    },
    "payload_contents": [
      {
        "byte_offset": 0,
        "data_type": "X2",
        "name": "navBbrMask",
        "description": "BBR sections to clear. The following special sets apply: 0x0000 Hot start 0x0001 Warm start 0xFFFF Cold start (see graphic below)"
      },
      {
        "byte_offset": 2,
        "data_type": "U1",
        "name": "resetMode",
        "description": "Reset Type",
        "value_descriptions": [
          {
            "value": "0x00",
            "description": "Hardware reset (watchdog) immediately"
          },
          {
            "value": "0x01",
            "description": "Controlled software reset"
          },
          {
            "value": "0x02",
            "description": "Controlled software reset (GNSS only)"
          },
          {
            "value": "0x04",
            "description": "Hardware reset (watchdog) after shutdown"
          },
          {
            "value": "0x08",
            "description": "Controlled GNSS stop"
          },
          {
            "value": "0x09",
            "description": "Controlled GNSS start"
          }
        ]
      },
      {
        "byte_offset": 3,
        "data_type": "U1",
        "name": "reserved1",
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
