{
  "ubx_message": {
    "name": "UBX-CFG-SPT",
    "class_id": 6,
    "message_id": 100,
    "description": "Configure and start a sensor production test",
    "message_type": "get_set",
    "comment": "The production test uses the built-in self-test capabilities of an attached sensor. This message is only supported if a sensor is directly connected to the u-blox receiver.",
    "payload_length": {
      "fixed": 12
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "name": "version",
        "data_type": "U1",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "byte_offset": 1,
        "name": "reserved1",
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 2,
        "name": "sensorId",
        "data_type": "U2",
        "description": "ID of the sensor to be tested; see UBX-MON-SPT for defined IDs"
      },
      {
        "byte_offset": 4,
        "name": "reserved2",
        "data_type": {
          "array_of": "U1",
          "count": 8
        },
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
