{
  "ubx_message": {
    "name": "UBX-CFG-TXSLOT",
    "class_id": "0x06",
    "message_id": "0x53",
    "description": "TX buffer time slots configuration",
    "message_type": "set",
    "payload_length": {
      "fixed": 16
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "name": "version",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "byte_offset": 1,
        "data_type": "X1",
        "name": "enable",
        "description": "Bitfield of ports for which the slots are enabled. (see graphic below)"
      },
      {
        "byte_offset": 2,
        "data_type": "U1",
        "name": "refTp",
        "description": "Reference timepulse source 0 - Timepulse 1 - Timepulse 2"
      },
      {
        "byte_offset": 3,
        "data_type": "U1",
        "name": "reserved1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 4,
        "data_type": {
          "array_of": "U4",
          "count": 3
        },
        "name": "end",
        "description": "End of timeslot in milliseconds after time pulse",
        "unit": "ms"
      }
    ]
  }
}
