{
  "ubx_messages": [
    {
      "name": "UBX-ESF-MEAS",
      "description": "External sensor fusion measurements",
      "message_type": "input_output",
      "class_id": "0x10",
      "message_id": "0x02",
      "payload_length": {
        "variable": {
          "base": 8,
          "formula": "8 + N*4",
          "N_description": "Number of 'data' elements in the repeated block. Calculated as (payload_length - 8) / 4."
        }
      },
      "fields": [
        {
          "byte_offset": 0,
          "data_type": "U4",
          "name": "timeTag",
          "description": "Time tag of measurement generated by external sensor"
        },
        {
          "byte_offset": 4,
          "data_type": "X2",
          "name": "flags",
          "description": "Flags. Set all unused bits to zero.",
          "bitfields": [
            {
              "name": "calibTtagValid",
              "bit_offset": 0,
              "num_bits": 1,
              "description": "Calibration time tag available. Always set to zero.",
              "fixed_value": 0
            },
            {
              "name": "timeMarkEdge",
              "bit_offset": 1,
              "num_bits": 1,
              "description": "Trigger on rising (0) or falling (1) edge of time mark signal",
              "value_descriptions": [
                {
                  "value": 0,
                  "description": "rising"
                },
                {
                  "value": 1,
                  "description": "falling"
                }
              ]
            },
            {
              "name": "timeMarkSent",
              "bit_offset": 2,
              "num_bits": 2,
              "description": "Time mark signal was supplied just prior to sending this message",
              "value_descriptions": [
                {
                  "value": 0,
                  "description": "none"
                },
                {
                  "value": 1,
                  "description": "on Ext0"
                },
                {
                  "value": 2,
                  "description": "on Ext1"
                }
              ]
            },
            {
              "name": "reserved1",
              "bit_offset": 4,
              "num_bits": 12,
              "description": "Reserved",
              "reserved": true
            }
          ]
        },
        {
          "byte_offset": 6,
          "data_type": "U2",
          "name": "id",
          "description": "Identification number of data provider"
        },
        {
          "byte_offset": 8,
          "name": "data_measurements",
          "description": "Repeated block of sensor measurements.",
          "array_count": {
            "variable": "N",
            "description": "Number of measurements. Derived from payload length: (payload_length - 8) / 4"
          },
          "elements": [
            {
              "name": "data",
              "data_type": "X4",
              "description": "Data measurement value and type",
              "bitfields": [
                {
                  "name": "dataField",
                  "bit_offset": 0,
                  "num_bits": 24,
                  "description": "Data value"
                },
                {
                  "name": "dataType",
                  "bit_offset": 24,
                  "num_bits": 6,
                  "description": "Type of data (0 = no data; 1..63 = data type)"
                },
                {
                  "name": "reserved1",
                  "bit_offset": 30,
                  "num_bits": 2,
                  "description": "Reserved",
                  "reserved": true
                }
              ]
            }
          ]
        }
      ]
    }
  ]
}
