{
  "ubx_message": {
    "name": "UBX-LOG-BATCH",
    "class_id": "0x21",
    "message_id": "0x11",
    "message_type": "polled",
    "description": "Batched data",
    "firmware_versions": [
      "u-blox 8 / u-blox M8 with protocol version 23.01"
    ],
    "payload_length": {
      "fixed": 100
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "name": "version",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "byte_offset": 1,
        "data_type": "X1",
        "name": "contentValid",
        "description": "Content validity flags",
        "bitfield_members": [
          {
            "name": "extraPvt",
            "description": "Extra PVT information is valid",
            "position": 0,
            "length": 1
          },
          {
            "name": "extraOdo",
            "description": "Odometer data is valid",
            "position": 1,
            "length": 1
          }
        ]
      },
      {
        "byte_offset": 2,
        "data_type": "U2",
        "name": "msgCnt",
        "description": "Message counter; increments for each sent UBX-LOG-BATCH message."
      },
      {
        "byte_offset": 4,
        "data_type": "U4",
        "name": "iTOW",
        "unit": "ms",
        "description": "GPS time of week of the navigation epoch. See the description of iTOW for details. Only valid if extraPvt is set."
      },
      {
        "byte_offset": 8,
        "data_type": "U2",
        "name": "year",
        "unit": "y",
        "description": "Year (UTC)"
      },
      {
        "byte_offset": 10,
        "data_type": "U1",
        "name": "month",
        "unit": "month",
        "description": "Month, range 1..12 (UTC)"
      },
      {
        "byte_offset": 11,
        "data_type": "U1",
        "name": "day",
        "unit": "d",
        "description": "Day of month, range 1..31 (UTC)"
      },
      {
        "byte_offset": 12,
        "data_type": "U1",
        "name": "hour",
        "unit": "h",
        "description": "Hour of day, range 0..23 (UTC)"
      },
      {
        "byte_offset": 13,
        "data_type": "U1",
        "name": "min",
        "unit": "min",
        "description": "Minute of hour, range 0..59 (UTC)"
      },
      {
        "byte_offset": 14,
        "data_type": "U1",
        "name": "sec",
        "unit": "s",
        "description": "Seconds of minute, range 0..60 (UTC)"
      },
      {
        "byte_offset": 15,
        "data_type": "X1",
        "name": "valid",
        "description": "Validity flags",
        "bitfield_members": [
          {
            "name": "validTime",
            "description": "1 = valid UTC Time of Day",
            "position": 0,
            "length": 1
          },
          {
            "name": "validDate",
            "description": "1 = valid UTC Date",
            "position": 1,
            "length": 1
          }
        ]
      },
      {
        "byte_offset": 16,
        "data_type": "U4",
        "name": "tAcc",
        "unit": "ns",
        "description": "Time accuracy estimate (UTC) Only valid if extraPvt is set."
      },
      {
        "byte_offset": 20,
        "data_type": "I4",
        "name": "fracSec",
        "unit": "ns",
        "description": "Fraction of second, range -1e9 .. 1e9 (UTC)"
      },
      {
        "byte_offset": 24,
        "data_type": "U1",
        "name": "fixType",
        "description": "GNSSfix Type: 0: no fix, 2: 2D-fix, 3: 3D-fix"
      },
      {
        "byte_offset": 25,
        "data_type": "X1",
        "name": "flags",
        "description": "Fix status flags",
        "bitfield_members": [
          {
            "name": "gnssFixOK",
            "description": "1 = valid fix (i.e within DOP & accuracy masks)",
            "position": 0,
            "length": 1
          },
          {
            "name": "diffSoln",
            "description": "1 = differential corrections were applied",
            "position": 1,
            "length": 1
          },
          {
            "name": "psmState",
            "description": "Power save mode state (0: PSM is not active, 1: Enabled, 2: Acquisition, 3: Tracking, 4: Power optimized tracking, 5: Inactive)",
            "position": 2,
            "length": 3
          }
        ]
      },
      {
        "byte_offset": 26,
        "data_type": "X1",
        "name": "flags2",
        "description": "Additional flags"
      },
      {
        "byte_offset": 27,
        "data_type": "U1",
        "name": "numSV",
        "description": "Number of satellites used in Nav Solution Only valid if extraPvt is set."
      },
      {
        "byte_offset": 28,
        "data_type": "I4",
        "name": "lon",
        "scale": {
          "multiplier": 1e-7
        },
        "unit": "deg",
        "description": "Longitude"
      },
      {
        "byte_offset": 32,
        "data_type": "I4",
        "name": "lat",
        "scale": {
          "multiplier": 1e-7
        },
        "unit": "deg",
        "description": "Latitude"
      },
      {
        "byte_offset": 36,
        "data_type": "I4",
        "name": "height",
        "unit": "mm",
        "description": "Height above ellipsoid"
      },
      {
        "byte_offset": 40,
        "data_type": "I4",
        "name": "hMSL",
        "unit": "mm",
        "description": "Height above mean sea level Only valid if extraPvt is set."
      },
      {
        "byte_offset": 44,
        "data_type": "U4",
        "name": "hAcc",
        "unit": "mm",
        "description": "Horizontal accuracy estimate"
      },
      {
        "byte_offset": 48,
        "data_type": "U4",
        "name": "vAcc",
        "unit": "mm",
        "description": "Vertical accuracy estimate Only valid if extraPvt is set."
      },
      {
        "byte_offset": 52,
        "data_type": "I4",
        "name": "velN",
        "unit": "mm/s",
        "description": "NED north velocity Only valid if extraPvt is set."
      },
      {
        "byte_offset": 56,
        "data_type": "I4",
        "name": "velE",
        "unit": "mm/s",
        "description": "NED east velocity Only valid if extraPvt is set."
      },
      {
        "byte_offset": 60,
        "data_type": "I4",
        "name": "velD",
        "unit": "mm/s",
        "description": "NED down velocity Only valid if extraPvt is set."
      },
      {
        "byte_offset": 64,
        "data_type": "I4",
        "name": "gSpeed",
        "unit": "mm/s",
        "description": "Ground Speed (2-D)"
      },
      {
        "byte_offset": 68,
        "data_type": "I4",
        "name": "headMot",
        "scale": {
          "multiplier": 1e-5
        },
        "unit": "deg",
        "description": "Heading of motion (2-D)"
      },
      {
        "byte_offset": 72,
        "data_type": "U4",
        "name": "sAcc",
        "unit": "mm/s",
        "description": "Speed accuracy estimate Only valid if extraPvt is set."
      },
      {
        "byte_offset": 76,
        "data_type": "U4",
        "name": "headAcc",
        "scale": {
          "multiplier": 1e-5
        },
        "unit": "deg",
        "description": "Heading accuracy estimate Only valid if extraPvt is set."
      },
      {
        "byte_offset": 80,
        "data_type": "U2",
        "name": "pDOP",
        "scale": {
          "multiplier": 0.01
        },
        "description": "Position DOP Only valid if extraPvt is set."
      },
      {
        "byte_offset": 82,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "name": "reserved1",
        "reserved": true
      },
      {
        "byte_offset": 84,
        "data_type": "U4",
        "name": "distance",
        "unit": "m",
        "description": "Ground distance since last reset Only valid if extraOdo is set."
      },
      {
        "byte_offset": 88,
        "data_type": "U4",
        "name": "totalDistance",
        "unit": "m",
        "description": "Total cumulative ground distance Only valid if extraOdo is set."
      },
      {
        "byte_offset": 92,
        "data_type": "U4",
        "name": "distanceStd",
        "unit": "m",
        "description": "Ground distance accuracy (1-sigma) Only valid if extraOdo is set."
      },
      {
        "byte_offset": 96,
        "data_type": {
          "array_of": "U1",
          "count": 4
        },
        "name": "reserved2",
        "reserved": true
      }
    ]
  }
}
