{
  "ubx_message": {
    "name": "UBX-MGA-BDS-UTC",
    "class_id": "0x13",
    "message_id": "0x03",
    "message_type": "input",
    "description": "BeiDou UTC assistance",
    "firmware_support": [
      "15",
      "15.01",
      "16",
      "17",
      "18",
      "19",
      "19.1",
      "19.2",
      "20",
      "20.01",
      "20.1",
      "20.2",
      "20.3",
      "22",
      "22.01",
      "23",
      "23.01"
    ],
    "comment": "This message allows the delivery of BeiDou UTC assistance to a receiver. See the description of AssistNow Online for details.",
    "payload_length": {
      "fixed": 20
    },
    "fields": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "name": "type",
        "description": "Message type (0x05 for this type)",
        "fixed_value": 5
      },
      {
        "byte_offset": 1,
        "data_type": "U1",
        "name": "version",
        "description": "Message version (0x00 for this version)"
      },
      {
        "byte_offset": 2,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "name": "reserved1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 4,
        "data_type": "I4",
        "name": "a0UTC",
        "scale": {
          "raw": "2^-30",
          "multiplier": 2.3283064365386963e-10
        },
        "unit": "s",
        "description": "BDT clock bias relative to UTC"
      },
      {
        "byte_offset": 8,
        "data_type": "I4",
        "name": "a1UTC",
        "scale": {
          "raw": "2^-50",
          "multiplier": 8.881784197001252e-16
        },
        "unit": "s/s",
        "description": "BDT clock rate relative to UTC"
      },
      {
        "byte_offset": 12,
        "data_type": "I1",
        "name": "dtLS",
        "unit": "s",
        "description": "Delta time due to leap seconds before the new leap second effective"
      },
      {
        "byte_offset": 13,
        "data_type": "U1",
        "name": "reserved2",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 14,
        "data_type": "U1",
        "name": "wnRec",
        "unit": "week",
        "description": "BeiDou week number of reception of this UTC parameter set (8-bit truncated)"
      },
      {
        "byte_offset": 15,
        "data_type": "U1",
        "name": "wnLSF",
        "unit": "week",
        "description": "Week number of the new leap second"
      },
      {
        "byte_offset": 16,
        "data_type": "U1",
        "name": "dN",
        "unit": "day",
        "description": "Day number of the new leap second"
      },
      {
        "byte_offset": 17,
        "data_type": "I1",
        "name": "dtLSF",
        "unit": "s",
        "description": "Delta time due to leap seconds after the new leap second effective"
      },
      {
        "byte_offset": 18,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "name": "reserved3",
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
