parts:
{
  "message_name": "UBX-MGA-GLO-ALM",
  "description": "GLONASS almanac assistance",
  "class_id": "0x13",
  "message_id": "0x06",
  "message_type": "input",
  "payload_length": {
    "fixed": 36
  },
  "payload_contents": [
    {
      "byte_offset": 0,
      "name": "type",
      "data_type": "U1",
      "description": "Message type (0x02 for this type)",
      "fixed_value": 2
    },
    {
      "byte_offset": 1,
      "name": "version",
      "data_type": "U1",
      "description": "Message version (0x00 for this version)",
      "fixed_value": 0
    },
    {
      "byte_offset": 2,
      "name": "svId",
      "data_type": "U1",
      "description": "GLONASS Satellite identifier (see Satellite Numbering)"
    },
    {
      "byte_offset": 3,
      "name": "reserved1",
      "data_type": "U1",
      "description": "Reserved",
      "reserved": true
    },
    {
      "byte_offset": 4,
      "name": "N",
      "data_type": "U2",
      "unit": "days",
      "description": "Reference calender day number of almanac within the four-year period (from string 5)"
    },
    {
      "byte_offset": 6,
      "name": "M",
      "data_type": "U1",
      "description": "Type of GLONASS satellite (1 indicates GLONASS-M)"
    },
    {
      "byte_offset": 7,
      "name": "C",
      "data_type": "U1",
      "description": "Unhealthy flag at instant of almanac upload (1 indicates operability of satellite)"
    },
    {
      "byte_offset": 8,
      "name": "tau",
      "data_type": "I2",
      "scale": {
        "raw": "2^-18",
        "multiplier": 3.814697265625e-06
      },
      "unit": "S",
      "description": "Coarse time correction to GLONASS time"
    },
    {
      "byte_offset": 10,
      "name": "epsilon",
      "data_type": "U2",
      "scale": {
        "raw": "2^-20",
        "multiplier": 9.5367431640625e-07
      },
      "description": "Eccentricity"
    },
    {
      "byte_offset": 12,
      "name": "lambda",
      "data_type": "I4",
      "scale": {
        "raw": "2^-20",
        "multiplier": 9.5367431640625e-07
      },
      "unit": "semi-circles",
      "description": "Longitude of the first (within the N-day) ascending node of satellite orbit in PC-90. 02 coordinate system"
    },
    {
      "byte_offset": 16,
      "name": "deltaI",
      "data_type": "I4",
      "scale": {
        "raw": "2^-20",
        "multiplier": 9.5367431640625e-07
      },
      "unit": "semi-circles",
      "description": "Correction to the mean value of inclination"
    },
    {
      "byte_offset": 20,
      "name": "tLambda",
      "data_type": "U4",
      "scale": {
        "raw": "2^-5",
        "multiplier": 0.03125
      },
      "unit": "S",
      "description": "Time of the first ascending node passage"
    },
    {
      "byte_offset": 24,
      "name": "deltaT",
      "data_type": "I4",
      "scale": {
        "raw": "2^-9",
        "multiplier": 0.001953125
      },
      "unit": "s/orbital period",
      "description": "Correction to the mean value of Draconian period"
    },
    {
      "byte_offset": 28,
      "name": "deltaDT",
      "data_type": "I1",
      "scale": {
        "raw": "2^-14",
        "multiplier": 6.103515625e-05
      },
      "unit": "s/orbital period^2",
      "description": "Rate of change of Draconian period"
    },
    {
      "byte_offset": 29,
      "name": "H",
      "data_type": "I1",
      "description": "Carrier frequency number of navigation RF signal, Range=(-7 .. 6)"
    },
    {
      "byte_offset": 30,
      "name": "omega",
      "data_type": "I2",
      "description": "Argument of perigee"
    },
    {
      "byte_offset": 32,
      "name": "reserved2",
      "data_type": {
        "array_of": "U1",
        "count": 4
      },
      "description": "Reserved",
      "reserved": true
    }
  ]
}
