parts:
{
  "class_id": "0x13",
  "message_id": "0x06",
  "name": "UBX-MGA-GLO-TIMEOFFSET",
  "description": "GLONASS auxiliary time offset assistance",
  "message_type": "input",
  "payload_length": {
    "fixed": 20
  },
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "U1",
      "name": "type",
      "description": "Message type (0x03 for this type)",
      "fixed_value": 3
    },
    {
      "byte_offset": 1,
      "data_type": "U1",
      "name": "version",
      "description": ""
    },
    {
      "byte_offset": 2,
      "data_type": "U1",
      "name": "svId",
      "description": "GLONASS Satellite identifier (see Satellite Numbering)"
    },
    {
      "byte_offset": 3,
      "data_type": "U1",
      "name": "reserved1",
      "description": "Reserved",
      "reserved": true
    },
    {
      "byte_offset": 4,
      "data_type": "U2",
      "name": "N",
      "unit": "days",
      "description": "Reference calender day number of almanac within the four-year period (from string 5)"
    },
    {
      "byte_offset": 6,
      "data_type": "U1",
      "name": "M",
      "description": "Type of GLONASS satellite (1 indicates GLONASS-M)"
    },
    {
      "byte_offset": 7,
      "data_type": "U1",
      "name": "C",
      "description": "Unhealthy flag at instant of almanac upload (1 indicates operability of satellite)"
    },
    {
      "byte_offset": 8,
      "data_type": "I2",
      "name": "tau",
      "scale": {
        "raw": "2^-18",
        "multiplier": 3.814697265625e-06
      },
      "unit": "s",
      "description": "Coarse time correction to GLONASS time"
    },
    {
      "byte_offset": 10,
      "data_type": "U2",
      "name": "epsilon",
      "scale": {
        "raw": "2^-20",
        "multiplier": 9.5367431640625e-07
      },
      "description": "Eccentricity"
    },
    {
      "byte_offset": 12,
      "data_type": "I4",
      "name": "lambda",
      "scale": {
        "raw": "2^-20",
        "multiplier": 9.5367431640625e-07
      },
      "unit": "semi-circles",
      "description": "Longitude of the first (within the N-day) ascending node of satellite orbit in PC-90.02 coordinate system"
    },
    {
      "byte_offset": 16,
      "data_type": "I4",
      "name": "deltaI",
      "scale": {
        "raw": "2^-20",
        "multiplier": 9.5367431640625e-07
      },
      "unit": "semi-circles",
      "description": "Correction to the mean value of inclination"
    }
  ]
}
