{
  "ubx_message": {
    "name": "UBX-MGA-INI-TIME-GNSS",
    "description": "Initial time assistance",
    "message_type": "input",
    "class_id": "0x13",
    "message_id": "0x40",
    "payload_length": {
      "fixed": 24
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "name": "type",
        "description": "Message type (0x11 for this type)",
        "fixed_value": 17
      },
      {
        "byte_offset": 1,
        "data_type": "U1",
        "name": "version",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "byte_offset": 2,
        "data_type": "X1",
        "name": "ref",
        "description": "Reference to be used to set time",
        "bitfield_fields": [
          {
            "name": "source",
            "position": 0,
            "length": 3,
            "description": "0: none, i.e. on receipt of message (will be inaccurate!), 1: relative to pulse sent to EXTINT0, 2: relative to pulse sent to EXTINT1, 3-15: reserved"
          },
          {
            "name": "fall",
            "position": 3,
            "length": 1,
            "description": "use falling edge of EXTINT pulse (default rising) - only if source is EXTINT"
          },
          {
            "name": "last",
            "position": 4,
            "length": 1,
            "description": "use last EXTINT pulse (default next pulse) - only if source is EXTINT"
          },
          {
            "name": "reserved_5",
            "position": 5,
            "length": 1,
            "reserved": true
          }
        ]
      },
      {
        "byte_offset": 3,
        "data_type": "U1",
        "name": "gnssId",
        "description": "Source of time information. Currently supported: 0: GPS time, 2: Galileo time, 3: BeiDou time, 6: GLONASS time: week = 834 + ((N4-1)*1461 + Nt)/7, tow = (((N4-1)*1461 + Nt) % 7) * 86400 + tod"
      },
      {
        "byte_offset": 4,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "name": "reserved1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 6,
        "data_type": "U2",
        "name": "week",
        "description": "GNSS week number"
      },
      {
        "byte_offset": 8,
        "data_type": "U4",
        "name": "tow",
        "unit": "s",
        "description": "GNSS time of week"
      },
      {
        "byte_offset": 12,
        "data_type": "U4",
        "name": "ns",
        "unit": "ns",
        "description": "GNSS time of week, nanosecond part from 0 to 999,999,999"
      },
      {
        "byte_offset": 16,
        "data_type": "U2",
        "name": "tAccS",
        "unit": "s",
        "description": "Seconds part of time accuracy"
      },
      {
        "byte_offset": 18,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "name": "reserved2",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 20,
        "data_type": "U4",
        "name": "tAccNs",
        "unit": "ns",
        "description": "Nanoseconds part of time accuracy, from 0 to 999,999,999"
      }
    ]
  }
}
