parts:
{
  "name": "UBX-MGA-INI-TIME-UTC",
  "class_id": "0x13",
  "message_id": "0x40",
  "message_type": "input",
  "description": "Initial time assistance",
  "payload_length": {
    "fixed": 24
  },
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "U1",
      "name": "type",
      "description": "Message type (0x10 for this type)",
      "fixed_value": 16
    },
    {
      "byte_offset": 1,
      "data_type": "U1",
      "name": "version",
      "description": "Message version (0x00 for this version)",
      "fixed_value": 0
    },
    {
      "byte_offset": 2,
      "data_type": "X1",
      "name": "ref",
      "description": "Reference to be used to set time (see graphic below)",
      "bitfield": [
        {
          "name": "source",
          "bits": 3,
          "description": "Source of reference time",
          "bit_offset": 0,
          "values": [
            {
              "value": 0,
              "description": "none, i.e. on receipt of message (will be inaccurate!)"
            },
            {
              "value": 1,
              "description": "relative to pulse sent to EXTINT0"
            },
            {
              "value": 2,
              "description": "relative to pulse sent to EXTINT1"
            },
            {
              "value": "3-15",
              "description": "reserved"
            }
          ]
        },
        {
          "name": "fall",
          "bits": 1,
          "description": "use falling edge of EXTINT pulse (default rising) - only if source is EXTINT",
          "bit_offset": 4
        },
        {
          "name": "last",
          "bits": 1,
          "description": "use last EXTINT pulse (default next pulse) - only if source is EXTINT",
          "bit_offset": 5
        }
      ]
    },
    {
      "byte_offset": 3,
      "data_type": "I1",
      "name": "leapSecs",
      "unit": "s",
      "description": "Number of leap seconds since 1980 (or 0x80 = -128 if unknown)"
    },
    {
      "byte_offset": 4,
      "data_type": "U2",
      "name": "year",
      "description": "Year"
    },
    {
      "byte_offset": 6,
      "data_type": "U1",
      "name": "month",
      "description": "Month, starting at 1"
    },
    {
      "byte_offset": 7,
      "data_type": "U1",
      "name": "day",
      "description": "Day, starting at 1"
    },
    {
      "byte_offset": 8,
      "data_type": "U1",
      "name": "hour",
      "description": "Hour, from 0 to 23"
    },
    {
      "byte_offset": 9,
      "data_type": "U1",
      "name": "minute",
      "description": "Minute, from 0 to 59"
    },
    {
      "byte_offset": 10,
      "data_type": "U1",
      "name": "second",
      "unit": "s",
      "description": "Seconds, from 0 to 59"
    },
    {
      "byte_offset": 11,
      "data_type": "U1",
      "name": "reserved1",
      "description": "Reserved",
      "reserved": true
    },
    {
      "byte_offset": 12,
      "data_type": "U4",
      "name": "ns",
      "unit": "ns",
      "description": "Nanoseconds, from 0 to 999,999,999"
    },
    {
      "byte_offset": 16,
      "data_type": "U2",
      "name": "tAccS",
      "unit": "s",
      "description": "Seconds part of time accuracy"
    },
    {
      "byte_offset": 18,
      "data_type": {
        "array_of": "U1",
        "count": 2
      },
      "name": "reserved2",
      "description": "Reserved",
      "reserved": true
    },
    {
      "byte_offset": 20,
      "data_type": "U4",
      "name": "tAccNs",
      "unit": "ns",
      "description": "Nanoseconds part of time accuracy, from 0 to 999,999,999"
    }
  ]
}
