{
  "ubx_message_extraction": {
    "message_name": "UBX-MON-BATCH",
    "class_id": "0x0A",
    "message_id": "0x32",
    "description": "Data batching buffer status",
    "firmware_support": "u-blox 8 / u-blox M8 with protocol version 23.01",
    "message_type": "polled",
    "payload_length": {
      "fixed": 12
    },
    "payload_content": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "name": "version",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "byte_offset": 1,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "name": "reserved1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 4,
        "data_type": "U2",
        "name": "fillLevel",
        "description": "Current buffer fill level, i.e. number of epochs currently stored"
      },
      {
        "byte_offset": 6,
        "data_type": "U2",
        "name": "dropsAll",
        "description": "Number of dropped epochs since startup Note: changing the batching configuration will reset this counter."
      },
      {
        "byte_offset": 8,
        "data_type": "U2",
        "name": "dropsSinceMon",
        "description": "Number of dropped epochs since last MON-BATCH message"
      },
      {
        "byte_offset": 10,
        "data_type": "U2",
        "name": "nextMsgCnt",
        "description": "The next retrieved UBX-LOG-BATCH will have this msgCnt value."
      }
    ]
  }
}
