{
  "ubx_message": {
    "name": "UBX-MON-HW2",
    "class_id": "0x0A",
    "message_id": "0x0B",
    "description": "Extended hardware status",
    "supported_on": [
      "u-blox 8/u-blox M8 protocol versions 15",
      "15.01",
      "16",
      "17",
      "18",
      "19",
      "19.1",
      "19.2",
      "20",
      "20.01",
      "20.1",
      "20.2",
      "20.3",
      "22",
      "22.01",
      "23",
      "23.01"
    ],
    "message_type": "periodic_polled",
    "comment": "Status of different aspects of the hardware such as Imbalance, Low-Level Configuration and POST Results. The first four parameters of this message represent the complex signal from the RF front end. The following rules of thumb apply: • The smaller the absolute value of the variable ofsI and ofsQ, the better. • Ideally, the magnitude of the I-part (magI) and the Q-part (magQ) of the complex signal should be the same.",
    "payload_length": {
      "fixed": 28
    },
    "fields": [
      {
        "byte_offset": 0,
        "data_type": "I1",
        "name": "ofsI",
        "description": "Imbalance of I-part of complex signal, scaled (-128 = max. negative imbalance, 127 = max. positive imbalance)"
      },
      {
        "byte_offset": 1,
        "data_type": "U1",
        "name": "magI",
        "description": "Magnitude of I-part of complex signal, scaled (0 = no signal, 255 = max. magnitude)"
      },
      {
        "byte_offset": 2,
        "data_type": "I1",
        "name": "ofsQ",
        "description": "Imbalance of Q-part of complex signal, scaled (-128 = max. negative imbalance, 127 = max. positive imbalance)"
      },
      {
        "byte_offset": 3,
        "data_type": "U1",
        "name": "magQ",
        "description": "Magnitude of Q-part of complex signal, scaled (0 = no signal, 255 = max. magnitude)"
      },
      {
        "byte_offset": 4,
        "data_type": "U1",
        "name": "cfgSource",
        "description": "Source of low-level configuration (114 = ROM, 111 = OTP, 112 = config pins, 102 = flash image)"
      },
      {
        "byte_offset": 5,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "name": "reserved1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 8,
        "data_type": "U4",
        "name": "lowLevCfg",
        "description": "Low-level configuration (obsolete in protocol versions greater than 15)"
      },
      {
        "byte_offset": 12,
        "data_type": {
          "array_of": "U1",
          "count": 8
        },
        "name": "reserved2",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 20,
        "data_type": "U4",
        "name": "postStatus",
        "description": "POST status word"
      },
      {
        "byte_offset": 24,
        "data_type": {
          "array_of": "U1",
          "count": 4
        },
        "name": "reserved3",
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
