parts:
{
  "ubx_message": {
    "name": "UBX-MON-PATCH",
    "class_id": "0x0A",
    "message_id": "0x27",
    "message_type": "polled",
    "payload_length": {
      "variable": {
        "base": 4,
        "formula": "4 + nEntries * 16"
      }
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "name": "version",
        "data_type": "U2",
        "description": "Message version (0x0001 for this version)",
        "fixed_value": 1
      },
      {
        "byte_offset": 2,
        "name": "nEntries",
        "data_type": "U2",
        "description": "Total number of reported patches"
      },
      {
        "byte_offset": 4,
        "name": "repeated_block",
        "item_type": "object",
        "count_field": "nEntries",
        "description": "Information for each reported patch",
        "fields": [
          {
            "byte_offset": 0,
            "name": "patchInfo",
            "data_type": "X4",
            "description": "Status information about the reported patch"
          },
          {
            "byte_offset": 4,
            "name": "comparatorNumber",
            "data_type": "U4",
            "description": "The number of the comparator"
          },
          {
            "byte_offset": 8,
            "name": "patchAddress",
            "data_type": "U4",
            "description": "The address that is targeted by the patch"
          },
          {
            "byte_offset": 12,
            "name": "patchData",
            "data_type": "U4",
            "description": "The data that is inserted at the patchAddress"
          }
        ]
      }
    ]
  }
}
