{
  "ubx_message": {
    "name": "UBX-MON-SMGR",
    "class_id": "0x0A",
    "message_id": "0x2E",
    "description": "Synchronization manager status",
    "message_type": "periodic_polled",
    "payload_length": {
      "fixed": 16
    },
    "payload": [
      {
        "byte_offset": 0,
        "name": "version",
        "data_type": "U1",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "byte_offset": 1,
        "name": "reserved1",
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "reserved": true,
        "description": "Reserved"
      },
      {
        "byte_offset": 4,
        "name": "iTOW",
        "data_type": "U4",
        "unit": "ms",
        "description": "Time of the week"
      },
      {
        "byte_offset": 8,
        "name": "intosc",
        "data_type": "X2",
        "description": "A bit mask, indicating the status of the local oscillator",
        "bitfields": [
          {
            "name": "intOscState",
            "bits": 3,
            "bit_offset": 0,
            "data_type": "U1",
            "description": "State of the oscillator:",
            "values": [
              {
                "value": 0,
                "description": "autonomous operation"
              },
              {
                "value": 1,
                "description": "calibration ongoing"
              },
              {
                "value": 2,
                "description": "oscillator is steered by the host"
              },
              {
                "value": 3,
                "description": "idle state"
              }
            ]
          },
          {
            "name": "intOscCalib",
            "bits": 1,
            "bit_offset": 3,
            "data_type": "U1",
            "description": "1 = oscillator gain is calibrated"
          },
          {
            "name": "intOscDisc",
            "bits": 1,
            "bit_offset": 4,
            "data_type": "U1",
            "description": "1 = signal is disciplined"
          },
          {
            "name": "reserved",
            "bits": 11,
            "bit_offset": 5,
            "reserved": true
          }
        ]
      },
      {
        "byte_offset": 10,
        "name": "extosc",
        "data_type": "X2",
        "description": "A bit mask, indicating the status of the external oscillator",
        "bitfields": [
          {
            "name": "extOscState",
            "bits": 3,
            "bit_offset": 0,
            "data_type": "U1",
            "description": "State of the oscillator:",
            "values": [
              {
                "value": 0,
                "description": "autonomous operation"
              },
              {
                "value": 1,
                "description": "calibration ongoing"
              },
              {
                "value": 2,
                "description": "oscillator is steered by the host"
              },
              {
                "value": 3,
                "description": "idle state"
              }
            ]
          },
          {
            "name": "extOscCalib",
            "bits": 1,
            "bit_offset": 3,
            "data_type": "U1",
            "description": "1 = oscillator gain is calibrated"
          },
          {
            "name": "extOscDisc",
            "bits": 1,
            "bit_offset": 4,
            "data_type": "U1",
            "description": "1 = signal is disciplined"
          },
          {
            "name": "reserved",
            "bits": 11,
            "bit_offset": 5,
            "reserved": true
          }
        ]
      },
      {
        "byte_offset": 12,
        "name": "discSrc",
        "data_type": "U1",
        "description": "Disciplining source identifier:",
        "values": [
          {
            "value": 0,
            "description": "internal oscillator"
          },
          {
            "value": 1,
            "description": "GNSS"
          },
          {
            "value": 2,
            "description": "EXTINT0"
          },
          {
            "value": 3,
            "description": "EXTINT1"
          },
          {
            "value": 4,
            "description": "internal oscillator measured by the host"
          },
          {
            "value": 5,
            "description": "external oscillator measured by the host"
          }
        ]
      },
      {
        "byte_offset": 13,
        "name": "gnss",
        "data_type": "X1",
        "description": "A bit mask, indicating the status of the GNSS",
        "bitfields": [
          {
            "name": "gnssAvail",
            "bits": 1,
            "bit_offset": 0,
            "data_type": "U1",
            "description": "1 = GNSS is present"
          },
          {
            "name": "reserved",
            "bits": 7,
            "bit_offset": 1,
            "reserved": true
          }
        ]
      },
      {
        "byte_offset": 14,
        "name": "extInt0",
        "data_type": "X1",
        "description": "A bit mask, indicating the status of the external input 0",
        "bitfields": [
          {
            "name": "extInt0Avail",
            "bits": 1,
            "bit_offset": 0,
            "data_type": "U1",
            "description": "1 = signal present at this input"
          },
          {
            "name": "extInt0Type",
            "bits": 1,
            "bit_offset": 1,
            "data_type": "U1",
            "description": "Source type:",
            "values": [
              {
                "value": 0,
                "description": "frequency"
              },
              {
                "value": 1,
                "description": "time"
              }
            ]
          },
          {
            "name": "extInt0FeedBack",
            "bits": 1,
            "bit_offset": 2,
            "data_type": "U1",
            "description": "This source is used as feedback of the external oscillator"
          },
          {
            "name": "reserved",
            "bits": 5,
            "bit_offset": 3,
            "reserved": true
          }
        ]
      },
      {
        "byte_offset": 15,
        "name": "extInt1",
        "data_type": "X1",
        "description": "A bit mask, indicating the status of the external input 1",
        "bitfields": [
          {
            "name": "extInt1Avail",
            "bits": 1,
            "bit_offset": 0,
            "data_type": "U1",
            "description": "1 = signal present at this input"
          },
          {
            "name": "extInt1Type",
            "bits": 1,
            "bit_offset": 1,
            "data_type": "U1",
            "description": "Source type:",
            "values": [
              {
                "value": 0,
                "description": "frequency"
              },
              {
                "value": 1,
                "description": "time"
              }
            ]
          },
          {
            "name": "extInt1FeedBack",
            "bits": 1,
            "bit_offset": 2,
            "data_type": "U1",
            "description": "This source is used as feedback of the external oscillator"
          },
          {
            "name": "reserved",
            "bits": 5,
            "bit_offset": 3,
            "reserved": true
          }
        ]
      }
    ]
  }
}
