parts:
{
  "ubx_message": {
    "name": "UBX-NAV-PVT",
    "class_id": "0x01",
    "message_id": "0x07",
    "message_type": "periodic_polled",
    "payload_length": {
      "fixed": 92
    },
    "fields": [
      {
        "byte_offset": 0,
        "data_type": "U4",
        "name": "iTOW",
        "unit": "ms",
        "description": "GPS time of week of the navigation epoch. See the description of iTOW for details."
      },
      {
        "byte_offset": 4,
        "data_type": "U2",
        "name": "year",
        "unit": "y",
        "description": "Year (UTC)"
      },
      {
        "byte_offset": 6,
        "data_type": "U1",
        "name": "month",
        "unit": "month",
        "description": "Month, range 1..12 (UTC)"
      },
      {
        "byte_offset": 7,
        "data_type": "U1",
        "name": "day",
        "unit": "d",
        "description": "Day of month, range 1..31 (UTC)"
      },
      {
        "byte_offset": 8,
        "data_type": "U1",
        "name": "hour",
        "unit": "h",
        "description": "Hour of day, range 0..23 (UTC)"
      },
      {
        "byte_offset": 9,
        "data_type": "U1",
        "name": "min",
        "unit": "min",
        "description": "Minute of hour, range 0..59 (UTC)"
      },
      {
        "byte_offset": 10,
        "data_type": "U1",
        "name": "sec",
        "unit": "s",
        "description": "Seconds of minute, range 0..60 (UTC)"
      },
      {
        "byte_offset": 11,
        "data_type": "X1",
        "name": "valid",
        "description": "Validity flags",
        "bitfield_members": [
          {
            "position": 0,
            "size": 1,
            "name": "validDate",
            "description": "1 = valid UTC Date (see Time Validity section for details)"
          },
          {
            "position": 1,
            "size": 1,
            "name": "validTime",
            "description": "1 = valid UTC time of day (see Time Validity section for details)"
          },
          {
            "position": 2,
            "size": 1,
            "name": "fullyResolved",
            "description": "1 = UTC time of day has been fully resolved (no seconds uncertainty). Cannot be used to check if time is completely solved."
          },
          {
            "position": 3,
            "size": 1,
            "name": "validMag",
            "description": "1 = valid magnetic declination"
          }
        ]
      },
      {
        "byte_offset": 12,
        "data_type": "U4",
        "name": "tAcc",
        "unit": "ns",
        "description": "Time accuracy estimate (UTC)"
      },
      {
        "byte_offset": 16,
        "data_type": "I4",
        "name": "nano",
        "unit": "ns",
        "description": "Fraction of second, range -1e9 .. 1e9 (UTC)"
      },
      {
        "byte_offset": 20,
        "data_type": "U1",
        "name": "fixType",
        "description": "GNSSfix Type: 0: no fix, 1: dead reckoning only, 2: 2D-fix, 3: 3D-fix, 4: GNSS + dead reckoning combined, 5: time only fix"
      },
      {
        "byte_offset": 21,
        "data_type": "X1",
        "name": "flags",
        "description": "Fix status flags",
        "bitfield_members": [
          {
            "position": 0,
            "size": 1,
            "name": "gnssFixOK",
            "description": "1 = valid fix (i.e within DOP & accuracy masks)"
          },
          {
            "position": 1,
            "size": 1,
            "name": "diffSoln",
            "description": "1 = differential corrections were applied"
          },
          {
            "position": 2,
            "size": 1,
            "name": "psmState",
            "description": "Power Save Mode state"
          },
          {
            "position": 3,
            "size": 1,
            "name": "headVehValid",
            "description": "1 = heading of vehicle is valid, only set if the receiver is in sensor fusion mode"
          },
          {
            "position": 5,
            "size": 2,
            "name": "carrSoln",
            "description": "Carrier phase range solution status: 0: no carrier phase range solution, 1: carrier phase range solution with floating ambiguities, 2: carrier phase range solution with fixed ambiguities (not supported in protocol versions less than 20)"
          }
        ]
      },
      {
        "byte_offset": 22,
        "data_type": "X1",
        "name": "flags2",
        "description": "Additional flags",
        "bitfield_members": [
          {
            "position": 5,
            "size": 1,
            "name": "confirmedAvai",
            "description": "1 = information about UTC Date and Time of Day validity confirmation is available (see Time Validity section for details)"
          },
          {
            "position": 6,
            "size": 1,
            "name": "confirmedDate",
            "description": "1 = UTC Date validity could be confirmed (see Time Validity section for details)"
          },
          {
            "position": 7,
            "size": 1,
            "name": "confirmedTime",
            "description": "1 = UTC Time of Day could be confirmed (see Time Validity section for details)"
          }
        ]
      },
      {
        "byte_offset": 23,
        "data_type": "U1",
        "name": "numSV",
        "description": "Number of satellites used in Nav Solution"
      },
      {
        "byte_offset": 24,
        "data_type": "I4",
        "name": "lon",
        "unit": "deg",
        "scaling": {
          "raw": "1e-7",
          "multiplier": 0.0000001
        },
        "description": "Longitude"
      },
      {
        "byte_offset": 28,
        "data_type": "I4",
        "name": "lat",
        "unit": "deg",
        "scaling": {
          "raw": "1e-7",
          "multiplier": 0.0000001
        },
        "description": "Latitude"
      },
      {
        "byte_offset": 32,
        "data_type": "I4",
        "name": "height",
        "unit": "mm",
        "description": "Height above ellipsoid"
      },
      {
        "byte_offset": 36,
        "data_type": "I4",
        "name": "hMSL",
        "unit": "mm",
        "description": "Height above mean sea level"
      },
      {
        "byte_offset": 40,
        "data_type": "U4",
        "name": "hAcc",
        "unit": "mm",
        "description": "Horizontal accuracy estimate"
      },
      {
        "byte_offset": 44,
        "data_type": "U4",
        "name": "vAcc",
        "unit": "mm",
        "description": "Vertical accuracy estimate"
      },
      {
        "byte_offset": 48,
        "data_type": "I4",
        "name": "velN",
        "unit": "mm/s",
        "description": "NED north velocity"
      },
      {
        "byte_offset": 52,
        "data_type": "I4",
        "name": "velE",
        "unit": "mm/s",
        "description": "NED east velocity"
      },
      {
        "byte_offset": 56,
        "data_type": "I4",
        "name": "velD",
        "unit": "mm/s",
        "description": "NED down velocity"
      },
      {
        "byte_offset": 60,
        "data_type": "I4",
        "name": "gSpeed",
        "unit": "mm/s",
        "description": "Ground Speed (2-D)"
      },
      {
        "byte_offset": 64,
        "data_type": "I4",
        "name": "headMot",
        "unit": "deg",
        "scaling": {
          "raw": "1e-5",
          "multiplier": 0.00001
        },
        "description": "Heading of motion (2-D)"
      },
      {
        "byte_offset": 68,
        "data_type": "U4",
        "name": "sAcc",
        "unit": "mm/s",
        "description": "Speed accuracy estimate"
      },
      {
        "byte_offset": 72,
        "data_type": "U4",
        "name": "headAcc",
        "unit": "deg",
        "scaling": {
          "raw": "1e-5",
          "multiplier": 0.00001
        },
        "description": "Heading accuracy estimate (both motion and vehicle)"
      },
      {
        "byte_offset": 76,
        "data_type": "U2",
        "name": "pDOP",
        "scaling": {
          "raw": "0.01",
          "multiplier": 0.01
        },
        "description": "Position DOP"
      },
      {
        "byte_offset": 78,
        "data_type": "X1",
        "name": "flags3",
        "description": "Additional flags",
        "bitfield_members": [
          {
            "position": 0,
            "size": 1,
            "name": "invalidLlh",
            "description": "1 = Invalid lon, lat, height and hMSL"
          }
        ]
      },
      {
        "byte_offset": 79,
        "data_type": {
          "array_of": "U1",
          "count": 5
        },
        "name": "reserved1",
        "reserved": true
      },
      {
        "byte_offset": 84,
        "data_type": "I4",
        "name": "headVeh",
        "unit": "deg",
        "scaling": {
          "raw": "1e-5",
          "multiplier": 0.00001
        },
        "description": "Heading of vehicle (2-D), this is only valid when headVehValid is set, otherwise the output is set to the heading of motion"
      },
      {
        "byte_offset": 88,
        "data_type": "I2",
        "name": "magDec",
        "unit": "deg",
        "scaling": {
          "raw": "1e-2",
          "multiplier": 0.01
        },
        "description": "Magnetic declination. Only supported in ADR 4.10 and later."
      },
      {
        "byte_offset": 90,
        "data_type": "U2",
        "name": "magAcc",
        "unit": "deg",
        "scaling": {
          "raw": "1e-2",
          "multiplier": 0.01
        },
        "description": "Magnetic declination accuracy. Only supported in ADR 4.10 and later."
      }
    ]
  }
}
