{
  "ubx_message_extraction": {
    "name": "UBX-NAV-SBAS",
    "class_id": "0x01",
    "message_id": "0x32",
    "description": "SBAS status data",
    "message_type": "periodic_polled",
    "firmware_support": "u-blox 8/u-blox M8 protocol versions 15, 15.01, 16, 17, 18, 19, 19.1, 19.2, 20, 20.01, 20.1, 20.2, 20.3, 22, 22.01, 23 and 23.01",
    "comment": "This message outputs the status of the SBAS sub system",
    "payload_length": {
      "variable": {
        "base": 12,
        "formula": "12 + cnt*12"
      }
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "data_type": "U4",
        "name": "iTOW",
        "unit": "ms",
        "description": "GPS time of week of the navigation epoch. See the description of iTOW for details."
      },
      {
        "byte_offset": 4,
        "data_type": "U1",
        "name": "geo",
        "description": "PRN Number of the GEO where correction and integrity data is used from"
      },
      {
        "byte_offset": 5,
        "data_type": "U1",
        "name": "mode",
        "description": "SBAS Mode",
        "values": [
          {
            "value": 0,
            "description": "Disabled"
          },
          {
            "value": 1,
            "description": "Enabled integrity"
          },
          {
            "value": 3,
            "description": "Enabled test mode"
          }
        ]
      },
      {
        "byte_offset": 6,
        "data_type": "I1",
        "name": "sys",
        "description": "SBAS System (WAAS/EGNOS/...)",
        "values": [
          {
            "value": -1,
            "description": "Unknown"
          },
          {
            "value": 0,
            "description": "WAAS"
          },
          {
            "value": 1,
            "description": "EGNOS"
          },
          {
            "value": 2,
            "description": "MSAS"
          },
          {
            "value": 3,
            "description": "GAGAN"
          },
          {
            "value": 16,
            "description": "GPS"
          }
        ]
      },
      {
        "byte_offset": 7,
        "data_type": "X1",
        "name": "service",
        "description": "SBAS Services available",
        "bitfield": [
          {
            "bit_offset": 0,
            "bit_length": 1,
            "name": "Ranging",
            "description": "GEO may be used as ranging source"
          },
          {
            "bit_offset": 1,
            "bit_length": 1,
            "name": "Corrections",
            "description": "GEO is providing correction data"
          },
          {
            "bit_offset": 2,
            "bit_length": 1,
            "name": "Integrity",
            "description": "GEO is providing integrity"
          },
          {
            "bit_offset": 3,
            "bit_length": 1,
            "name": "Testmode",
            "description": "GEO is in test mode"
          },
          {
            "bit_offset": 4,
            "bit_length": 1,
            "name": "Bad",
            "description": "Problem with signal or broadcast data indicated"
          },
          {
            "bit_offset": 5,
            "bit_length": 3,
            "name": "reserved_0",
            "reserved": true
          }
        ]
      },
      {
        "byte_offset": 8,
        "data_type": "U1",
        "name": "cnt",
        "description": "Number of SV data following"
      },
      {
        "byte_offset": 9,
        "data_type": "X1",
        "name": "statusFlags",
        "description": "SBAS status flags",
        "bitfield": [
          {
            "bit_offset": 0,
            "bit_length": 1,
            "name": "integrityUsed",
            "description": "SBAS integrity used",
            "values": [
              {
                "value": 0,
                "description": "Unknown"
              },
              {
                "value": 1,
                "description": "Integrity information is not available or SBAS integrity is not enabled"
              },
              {
                "value": 2,
                "description": "Receiver uses only GPS satellites for which integrity information is available"
              }
            ]
          },
          {
            "bit_offset": 1,
            "bit_length": 7,
            "name": "reserved_0",
            "reserved": true
          }
        ]
      },
      {
        "byte_offset": 10,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "name": "reserved1",
        "reserved": true
      },
      {
        "byte_offset": 12,
        "is_repeated": true,
        "num_repeats_field": "cnt",
        "fields": [
          {
            "byte_offset": 0,
            "data_type": "U1",
            "name": "svid",
            "description": "SV ID"
          },
          {
            "byte_offset": 1,
            "data_type": "U1",
            "name": "flags",
            "description": "Flags for this SV"
          },
          {
            "byte_offset": 2,
            "data_type": "U1",
            "name": "udre",
            "description": "Monitoring status"
          },
          {
            "byte_offset": 3,
            "data_type": "U1",
            "name": "svSys",
            "description": "System (WAAS/EGNOS/...) (same as SYS)"
          },
          {
            "byte_offset": 4,
            "data_type": "U1",
            "name": "svService",
            "description": "Services available (same as SERVICE)"
          },
          {
            "byte_offset": 5,
            "data_type": "U1",
            "name": "reserved2",
            "reserved": true
          },
          {
            "byte_offset": 6,
            "data_type": "I2",
            "name": "prc",
            "unit": "cm",
            "description": "Pseudo Range correction in [cm]"
          },
          {
            "byte_offset": 8,
            "data_type": {
              "array_of": "U1",
              "count": 2
            },
            "name": "reserved3",
            "reserved": true
          },
          {
            "byte_offset": 10,
            "data_type": "I2",
            "name": "ic",
            "unit": "cm",
            "description": "Ionosphere correction in [cm]"
          }
        ]
      }
    ]
  }
}
