{
  "class_id": "0x01",
  "message_id": "0x23",
  "name": "UBX-NAV-TIMEGLO",
  "description": "GLONASS time solution",
  "message_type": "periodic_polled",
  "comment": "This message reports the precise GLO time of the most recent navigation solution including validity flags and an accuracy estimate.",
  "payload_length": {
    "fixed": 20
  },
  "fields": [
    {
      "byte_offset": 0,
      "name": "iTow",
      "data_type": "U4",
      "unit": "ms",
      "description": "GPS time of week of the navigation epoch. See the description of iTOW for details."
    },
    {
      "byte_offset": 4,
      "name": "TOD",
      "data_type": "U4",
      "unit": "s",
      "description": "GLONASS time of day (rounded to integer seconds)"
    },
    {
      "byte_offset": 8,
      "name": "fTOD",
      "data_type": "I4",
      "unit": "ns",
      "description": "Fractional part of TOD (range: +/- 500000000). The precise GLONASS time of day in seconds is: TOD + fTOD * 1e-9"
    },
    {
      "byte_offset": 12,
      "name": "Nt",
      "data_type": "U2",
      "unit": "days",
      "description": "Current date (range: 1-1461), starting at 1 from the 1st Jan of the year indicated by N4 and ending at 1461 at the 31st Dec of the third year after that indicated by N4"
    },
    {
      "byte_offset": 14,
      "name": "N4",
      "data_type": "U1",
      "description": "Four-year interval number starting from 1996 (1=1996, 2=2000, 3=2004...)"
    },
    {
      "byte_offset": 15,
      "name": "valid",
      "data_type": "X1",
      "description": "Validity flags (see graphic below)"
    },
    {
      "byte_offset": 16,
      "name": "tAcc",
      "data_type": "U4",
      "unit": "ns",
      "description": "Time Accuracy Estimate"
    }
  ]
}
