parts:
{
  "name": "UBX-RXM-IMES",
  "class_id": "0x02",
  "message_id": "0x61",
  "description": "Indoor Messaging System information",
  "message_type": "periodic_polled",
  "payload_length": {
    "variable": {
      "base": 4,
      "formula": "4 + 44 * numTx"
    }
  },
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "U1",
      "name": "numTx",
      "description": "Number of transmitters contained in the message"
    },
    {
      "byte_offset": 1,
      "data_type": "U1",
      "name": "version",
      "fixed_value": 1,
      "description": "Message version (0x01 for this version)"
    },
    {
      "byte_offset": 2,
      "data_type": {
        "array_of": "U1",
        "count": 2
      },
      "name": "reserved1",
      "reserved": true,
      "description": "Reserved"
    }
  ],
  "repeated_block": {
    "count_field": "numTx",
    "block_length": 44,
    "fields": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "name": "reserved2",
        "reserved": true,
        "description": "Reserved"
      },
      {
        "byte_offset": 1,
        "data_type": "U1",
        "name": "txId",
        "description": "Transmitter identifier"
      },
      {
        "byte_offset": 2,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "name": "reserved3",
        "reserved": true,
        "description": "Reserved"
      },
      {
        "byte_offset": 5,
        "data_type": "U1",
        "name": "cno",
        "unit": "dBHz",
        "description": "Carrier to Noise Ratio (Signal Strength)"
      },
      {
        "byte_offset": 6,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "name": "reserved4",
        "reserved": true,
        "description": "Reserved"
      },
      {
        "byte_offset": 8,
        "data_type": "I4",
        "name": "doppler",
        "scaling": {
          "multiplier": "2^-12"
        },
        "unit": "Hz",
        "description": "Doppler frequency with respect to 1575.4282MHz [IIIII.FFF Hz]"
      },
      {
        "byte_offset": 12,
        "data_type": "X4",
        "name": "position1_1",
        "description": "Position 1 Frame (part 1/2)",
        "composite_fields": [
          {
            "bit_offset": 0,
            "bit_length": 31,
            "data_type": "I4",
            "name": "pos1Lat",
            "scaling": {
              "multiplier": "180/2^23"
            },
            "unit": "deg",
            "description": "Latitude [deg* (180/2^23)]"
          },
          {
            "bit_offset": 0,
            "bit_length": 8,
            "data_type": "U1",
            "name": "pos1Floor",
            "scaling": {
              "multiplier": "1.0"
            },
            "unit": "floor resolution",
            "offset": -50,
            "description": "Floor number [1.0 floor resolution] (Offset: -50 floor)"
          }
        ]
      },
      {
        "byte_offset": 16,
        "data_type": "X4",
        "name": "position1_2",
        "description": "Position 1 Frame (part 2/2)",
        "composite_fields": [
          {
            "bit_offset": 0,
            "bit_length": 24,
            "data_type": "I4",
            "name": "pos1Lon",
            "scaling": {
              "multiplier": "360/2^24"
            },
            "unit": "deg",
            "description": "Longitude [deg* (360/2^24)]"
          },
          {
            "bit_offset": 24,
            "bit_length": 1,
            "data_type": "U1",
            "name": "pos1Valid",
            "description": "Position 1 Frame valid"
          }
        ]
      },
      {
        "byte_offset": 20,
        "data_type": "X4",
        "name": "position2_1",
        "description": "Position 2 Frame (part 1/3)",
        "composite_fields": [
          {
            "bit_offset": 0,
            "bit_length": 8,
            "data_type": "U1",
            "name": "pos2Floor",
            "scaling": {
              "multiplier": "0.5"
            },
            "unit": "floor resolution",
            "offset": -50,
            "description": "Floor number [0.5 floor resolution] (Offset: -50 floor)"
          },
          {
            "bit_offset": 9,
            "bit_length": 12,
            "data_type": "I2",
            "name": "pos2Alt",
            "scaling": {
              "multiplier": "1.0"
            },
            "unit": "m",
            "offset": -95,
            "description": "Altitude [m] (Offset: -95m)"
          },
          {
            "bit_offset": 21,
            "bit_length": 2,
            "data_type": "U1",
            "name": "pos2Acc",
            "description": "Accuracy Index (0:undef, 1:<7m, 2:<15m, 3:>15m)"
          },
          {
            "bit_offset": 23,
            "bit_length": 1,
            "data_type": "U1",
            "name": "pos2Valid",
            "description": "Position 2 Frame valid"
          }
        ]
      },
      {
        "byte_offset": 24,
        "data_type": "I4",
        "name": "lat",
        "scaling": {
          "multiplier": "180/2^24"
        },
        "unit": "deg",
        "description": "Latitude, Position 2 Frame (part 2/3)"
      },
      {
        "byte_offset": 28,
        "data_type": "I4",
        "name": "lon",
        "scaling": {
          "multiplier": "360/2^25"
        },
        "unit": "deg",
        "description": "Longitude, Position 2 Frame (part 3/3)"
      },
      {
        "byte_offset": 32,
        "data_type": "X4",
        "name": "shortIdFrame",
        "description": "Short ID Frame",
        "composite_fields": [
          {
            "bit_offset": 0,
            "bit_length": 11,
            "data_type": "U2",
            "name": "shortId",
            "description": "Short ID"
          },
          {
            "bit_offset": 11,
            "bit_length": 1,
            "data_type": "U1",
            "name": "shortValid",
            "description": "Short ID Frame valid"
          },
          {
            "bit_offset": 12,
            "bit_length": 1,
            "data_type": "U1",
            "name": "shortBoundary",
            "description": "Boundary Bit"
          }
        ]
      },
      {
        "byte_offset": 36,
        "data_type": "U4",
        "name": "mediumIdLSB",
        "description": "Medium ID LSB, Medium ID Frame (part 1/2)"
      },
      {
        "byte_offset": 40,
        "data_type": "X4",
        "name": "mediumId_2",
        "description": "Medium ID Frame (part 2/2)",
        "composite_fields": [
          {
            "bit_offset": 0,
            "bit_length": 2,
            "data_type": "U1",
            "name": "mediumIdMSB",
            "description": "Medium ID MSB"
          },
          {
            "bit_offset": 1,
            "bit_length": 1,
            "data_type": "U1",
            "name": "mediumValid",
            "description": "Medium ID Frame valid"
          },
          {
            "bit_offset": 2,
            "bit_length": 1,
            "data_type": "U1",
            "name": "mediumBoundary",
            "description": "Boundary Bit"
          }
        ]
      }
    ]
  }
}
