{
  "name": "UBX-RXM-RTCM",
  "description": "RTCM input status",
  "message_class_id": "0x02",
  "message_id": "0x32",
  "message_type": "output",
  "firmware_support": "u-blox 8 / u-blox M8 protocol versions 20.01, 20.1, 20.2 and 20.3",
  "comment": "This message shows info on a received RTCM input message. It is output upon successful parsing of an RTCM input message, irrespective of whether the RTCM message is supported or not by the receiver.",
  "payload_length": {
    "fixed": 8
  },
  "fields": [
    {
      "byte_offset": 0,
      "name": "version",
      "data_type": "U1",
      "description": "Message version (0x02 for this version)",
      "fixed_value": 2
    },
    {
      "byte_offset": 1,
      "name": "flags",
      "data_type": "X1",
      "description": "RTCM input status flags",
      "bitfields": [
        {
          "name": "reserved",
          "bits": 1,
          "description": "Reserved"
        },
        {
          "name": "crcFailed",
          "bits": 1,
          "description": "0 when RTCM message received and passed CRC check, 1 when failed, in which case refStation and msgType might be corrupted and misleading"
        },
        {
          "name": "msgUsed",
          "bits": 1,
          "description": "2 = RTCM message used successfully by the receiver, 1 = not used, 0 = do not know"
        }
      ]
    },
    {
      "byte_offset": 2,
      "name": "subType",
      "data_type": "U2",
      "description": "Message subtype, only applicable to u-blox proprietary RTCM message 4072 (not available on all products)"
    },
    {
      "byte_offset": 4,
      "name": "refStation",
      "data_type": "U2",
      "description": "Reference station ID: For RTCM 2.3: Reference station ID of the received RTCM 2 input message. Valid range 0-1023. For RTCM 3.3: Reference station ID (DF003) of the received RTCM input message. Valid range 0-4095. Reported only for the standard RTCM messages that include the DF003 field and for the u-blox proprietary RTCM messages 4072.x. For all other messages, reports 0xFFFF."
    },
    {
      "byte_offset": 6,
      "name": "msgType",
      "data_type": "U2",
      "description": "Message type"
    }
  ]
}
