parts:
{
  "message_name": "UBX-RXM-SFRBX",
  "message_class": "0x02",
  "message_id": "0x13",
  "message_type": "output",
  "description": "Broadcast navigation data subframe",
  "payload_length": {
    "variable": {
      "base": 8,
      "formula": "8 + 4 * numWords"
    }
  },
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "U1",
      "name": "gnssId",
      "description": "GNSS identifier (see Satellite Numbering)"
    },
    {
      "byte_offset": 1,
      "data_type": "U1",
      "name": "svId",
      "description": "Satellite identifier (see Satellite Numbering)"
    },
    {
      "byte_offset": 2,
      "data_type": "U1",
      "name": "reserved1",
      "description": "Reserved",
      "reserved": true
    },
    {
      "byte_offset": 3,
      "data_type": "U1",
      "name": "freqId",
      "description": "Only used for GLONASS: This is the frequency slot + 7 (range from 0 to 13)"
    },
    {
      "byte_offset": 4,
      "data_type": "U1",
      "name": "numWords",
      "description": "The number of data words contained in this message (up to 10, for currently supported signals)"
    },
    {
      "byte_offset": 5,
      "data_type": "U1",
      "name": "chn",
      "description": "The tracking channel number the message was received on"
    },
    {
      "byte_offset": 6,
      "data_type": "U1",
      "name": "version",
      "description": "Message version (0x02 for this version)",
      "fixed_value": 2
    },
    {
      "byte_offset": 7,
      "data_type": "U1",
      "name": "reserved2",
      "description": "Reserved",
      "reserved": true
    },
    {
      "byte_offset": 8,
      "data_type": {
        "array_of": "U4",
        "count_field": "numWords"
      },
      "name": "dwrd",
      "description": "The data words"
    }
  ]
}
