parts:
{
  "name": "UBX-RXM-SVSI",
  "class_id": "0x02",
  "message_id": "0x20",
  "description": "SV status info",
  "message_type": "periodic_polled",
  "payload_length": {
    "variable": {
      "base": 8,
      "formula": "8 + 6 * numSV"
    }
  },
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "U4",
      "name": "iTow",
      "unit": "ms",
      "description": "GPS time of week of the navigation epoch. See the description of iTOW for details."
    },
    {
      "byte_offset": 4,
      "data_type": "I2",
      "name": "week",
      "unit": "weeks",
      "description": "GPS week number of the navigation epoch"
    },
    {
      "byte_offset": 6,
      "data_type": "U1",
      "name": "numVis",
      "description": "Number of visible satellites"
    },
    {
      "byte_offset": 7,
      "data_type": "U1",
      "name": "numSV",
      "description": "Number of per-SV data blocks following"
    },
    {
      "byte_offset": 8,
      "data_type": {
        "array_of": {
          "fields": [
            {
              "byte_offset": 0,
              "data_type": "U1",
              "name": "svid",
              "description": "Satellite ID"
            },
            {
              "byte_offset": 1,
              "data_type": "X1",
              "name": "svFlag",
              "description": "Information Flags (see graphic below)",
              "bitfield": [
                {
                  "name": "reserved1",
                  "mask": "0xC0",
                  "description": "Reserved"
                },
                {
                  "name": "ura",
                  "mask": "0x30",
                  "data_type": "U1",
                  "description": "Figure of Merit (URA) range 0..15"
                },
                {
                  "name": "healthy",
                  "mask": "0x08",
                  "data_type": "U1",
                  "description": "SV healthy flag"
                },
                {
                  "name": "ephVal",
                  "mask": "0x04",
                  "data_type": "U1",
                  "description": "Ephemeris valid"
                },
                {
                  "name": "almVal",
                  "mask": "0x02",
                  "data_type": "U1",
                  "description": "Almanac valid"
                },
                {
                  "name": "notAvail",
                  "mask": "0x01",
                  "data_type": "U1",
                  "description": "SV not available"
                }
              ]
            },
            {
              "byte_offset": 2,
              "data_type": "I2",
              "name": "azim",
              "description": "Azimuth"
            },
            {
              "byte_offset": 4,
              "data_type": "I1",
              "name": "elev",
              "description": "Elevation"
            },
            {
              "byte_offset": 5,
              "data_type": "X1",
              "name": "age",
              "description": "Age of Almanac and Ephemeris: (see graphic below)",
              "bitfield": [
                {
                  "name": "ephAge",
                  "mask": "0xF0",
                  "data_type": "U1",
                  "description": "Age of EPH in hours offset by 4. i.e. the reference time may be in the future: ageOfEph = ((age & 0xf0) >> 4) - 4"
                },
                {
                  "name": "almAge",
                  "mask": "0x0F",
                  "data_type": "U1",
                  "description": "Age of ALM in days offset by 4 i.e. the reference time may be in the future: ageOfAlm = (age & 0x0f) - 4"
                }
              ]
            }
          ]
        },
        "count": {
          "field": "numSV"
        },
        "name": "sv_data_blocks"
      }
    }
  ]
}
