{
  "name": "UBX-TIM-TM2",
  "class_id": "0x0D",
  "message_id": "0x03",
  "description": "Time mark data",
  "message_type": "periodic_polled",
  "payload_length": {
    "fixed": 28
  },
  "comment": "This message contains information for high precision time stamping / pulse counting. The delay figures and timebase given in UBX-CFG-TP5 are also applied to the time results output in this message.",
  "firmware_supported": "u-blox 8 / u-blox M8 protocol versions 15, 15.01, 16, 17, 18, 19, 19.1, 19.2, 20, 20.01, 20.1, 20.2, 20.3, 22, 22.01, 23 and 23.01",
  "fields": [
    {
      "byte_offset": 0,
      "name": "ch",
      "data_type": "U1",
      "description": "Channel (i.e. EXTINT) upon which the pulse was measured"
    },
    {
      "byte_offset": 1,
      "name": "flags",
      "data_type": "X1",
      "description": "Bitmask (see graphic below)",
      "bitfield_flags": [
        {
          "name": "mode",
          "position": 0,
          "length": 1,
          "description": "0=single, 1=running"
        },
        {
          "name": "run",
          "position": 1,
          "length": 1,
          "description": "0=armed, 1=stopped"
        },
        {
          "name": "reserved1",
          "position": 2,
          "length": 1,
          "description": "Reserved",
          "reserved": true
        },
        {
          "name": "newFallingEdge",
          "position": 3,
          "length": 1,
          "description": "New falling edge detected"
        },
        {
          "name": "timeBase",
          "position": 4,
          "length": 2,
          "description": "0=Time base is Receiver time, 1=Time base is GNSS time (the system according to the configuration in UBX-CFG-TP5 for tpldx=0), 2=Time base is UTC (the variant according to the configuration in UBX-CFG-NAV5)"
        },
        {
          "name": "time",
          "position": 6,
          "length": 1,
          "description": "0=Time is not valid, 1=Time is valid (Valid GNSS fix)"
        },
        {
          "name": "newRisingEdge",
          "position": 7,
          "length": 1,
          "description": "New rising edge detected"
        }
      ]
    },
    {
      "byte_offset": 2,
      "name": "count",
      "data_type": "U2",
      "description": "Rising edge counter"
    },
    {
      "byte_offset": 4,
      "name": "wnR",
      "data_type": "U2",
      "description": "Week number of last rising edge"
    },
    {
      "byte_offset": 6,
      "name": "wnF",
      "data_type": "U2",
      "description": "Week number of last falling edge"
    },
    {
      "byte_offset": 8,
      "name": "towMsR",
      "data_type": "U4",
      "unit": "ms",
      "description": "Tow of rising edge"
    },
    {
      "byte_offset": 12,
      "name": "towSubMsR",
      "data_type": "U4",
      "unit": "ns",
      "description": "Millisecond fraction of tow of rising edge in nanoseconds"
    },
    {
      "byte_offset": 16,
      "name": "towMsF",
      "data_type": "U4",
      "unit": "ms",
      "description": "Tow of falling edge"
    },
    {
      "byte_offset": 20,
      "name": "towSubMsF",
      "data_type": "U4",
      "unit": "ns",
      "description": "Millisecond fraction of tow of falling edge in nanoseconds"
    },
    {
      "byte_offset": 24,
      "name": "accEst",
      "data_type": "U4",
      "unit": "ns",
      "description": "Accuracy estimate"
    }
  ]
}
