parts:
{
  "class_id": "0x0D",
  "message_id": "0x01",
  "name": "UBX-TIM-TP",
  "description": "Time pulse time data",
  "message_type": "periodic_polled",
  "payload_length": {
    "fixed": 16
  },
  "firmware_support": [
    "u-blox 8/u-blox M8 protocol versions 15",
    "15.01",
    "16",
    "17",
    "18",
    "19",
    "19.1",
    "19.2",
    "20",
    "20.01",
    "20.1",
    "20.2",
    "20.3",
    "22"
  ],
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "U4",
      "name": "towMS",
      "description": "Time pulse time of week according to time base",
      "unit": "ms"
    },
    {
      "byte_offset": 4,
      "data_type": "U4",
      "name": "towSubMS",
      "description": "Submillisecond part of towMS",
      "scale": {
        "raw": "2^-32",
        "multiplier": 0.0000000002328306436538696
      },
      "unit": "ms"
    },
    {
      "byte_offset": 8,
      "data_type": "I4",
      "name": "qErr",
      "description": "Quantization error of time pulse",
      "unit": "ps"
    },
    {
      "byte_offset": 12,
      "data_type": "U2",
      "name": "week",
      "description": "Time pulse week number according to time base",
      "unit": "weeks"
    },
    {
      "byte_offset": 14,
      "data_type": "X1",
      "name": "flags",
      "description": "Flags",
      "bitfield": [
        {
          "name": "timeBase",
          "bits": "0",
          "description": "Time base type",
          "values": {
            "0": "Time base is GNSS",
            "1": "Time base is UTC"
          }
        },
        {
          "name": "utc",
          "bits": "1",
          "description": "UTC availability",
          "values": {
            "0": "UTC not available",
            "1": "UTC available"
          }
        },
        {
          "name": "raim",
          "bits": "2",
          "description": "(T)RAIM information (0 = RAIM not active or information not available, 1 = RAIM active)",
          "values": {
            "0": "RAIM not active or information not available",
            "1": "RAIM active"
          }
        },
        {
          "name": "reserved",
          "bits": "3",
          "reserved": true
        },
        {
          "name": "qErrInvalid",
          "bits": "4",
          "description": "Quantization error validity",
          "values": {
            "0": "Quantization error valid",
            "1": "Quantization error invalid"
          }
        },
        {
          "name": "reserved",
          "bits": "5-7",
          "reserved": true
        }
      ]
    },
    {
      "byte_offset": 15,
      "data_type": "X1",
      "name": "refInfo",
      "description": "Time reference information",
      "bitfield": [
        {
          "name": "timeRefGnss",
          "bits": "0-3",
          "description": "GNSS reference information. Only valid if time base is GNSS (timeBase=0).",
          "values": {
            "0": "GPS",
            "1": "GLONASS",
            "2": "BeiDou",
            "3": "Galileo",
            "15": "Unknown"
          }
        },
        {
          "name": "utcStandard",
          "bits": "4-7",
          "description": "UTC standard identifier. Only valid if time base is UTC (timeBase=1).",
          "values": {
            "0": "Information not available",
            "1": "Communications Research Laboratory (CRL), Tokyo, Japan",
            "2": "National Institute of Standards and Technology (NIST)",
            "3": "U.S. Naval Observatory (USNO)",
            "4": "International Bureau of Weights and Measures (BIPM)",
            "5": "European laboratories",
            "6": "Former Soviet Union (SU)",
            "7": "National Time Service Center (NTSC), China",
            "15": "Unknown"
          }
        }
      ]
    }
  ]
}
