{
  "class_id": "0x0D",
  "message_id": "0x06",
  "message_name": "UBX-TIM-VRFY",
  "description": "Sourced time verification",
  "message_type": "periodic_polled",
  "payload_length": {
    "fixed": 20
  },
  "payload_content": [
    {
      "byte_offset": 0,
      "name": "itow",
      "data_type": "I4",
      "unit": "ms",
      "description": "integer millisecond tow received by source"
    },
    {
      "byte_offset": 4,
      "name": "frac",
      "data_type": "I4",
      "unit": "ns",
      "description": "sub-millisecond part of tow"
    },
    {
      "byte_offset": 8,
      "name": "deltaMs",
      "data_type": "I4",
      "unit": "ms",
      "description": "integer milliseconds of delta time (current time minus sourced time)"
    },
    {
      "byte_offset": 12,
      "name": "deltaNs",
      "data_type": "I4",
      "unit": "ns",
      "description": "Sub-millisecond part of delta time"
    },
    {
      "byte_offset": 16,
      "name": "wno",
      "data_type": "U2",
      "unit": "week",
      "description": "Week number"
    },
    {
      "byte_offset": 18,
      "name": "flags",
      "data_type": "X1",
      "description": "Flags (see graphic below)",
      "bitfield_flags": [
        {
          "name": "src",
          "bits": "2:0",
          "description": "Aiding time source",
          "values": [
            {
              "value": 0,
              "description": "no time aiding done"
            },
            {
              "value": 2,
              "description": "source was RTC"
            },
            {
              "value": 3,
              "description": "source was assistance data"
            }
          ]
        }
      ]
    },
    {
      "byte_offset": 19,
      "name": "reserved1",
      "data_type": "U1",
      "reserved": true,
      "description": "Reserved"
    }
  ]
}
