parts:
{
  "ubx_message": {
    "name": "UBX-MON-HW3",
    "class_id": "0x0a",
    "message_id": "0x37",
    "message_type": "periodic_polled",
    "description": "I/O pin status",
    "payload_length": {
      "variable": {
        "base": 22,
        "formula": "22 + nPins*6"
      }
    },
    "fields": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "name": "version",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "byte_offset": 1,
        "data_type": "U1",
        "name": "nPins",
        "description": "The number of I/O pins included"
      },
      {
        "byte_offset": 2,
        "data_type": "X1",
        "name": "flags",
        "description": "Flags",
        "bit_fields": [
          {
            "bit_offset": 0,
            "data_type": "U1",
            "name": "rtcCalib",
            "description": "RTC is calibrated"
          },
          {
            "bit_offset": 1,
            "data_type": "U1",
            "name": "safeBoot",
            "description": "Safeboot mode (0 = inactive, 1 = active)"
          },
          {
            "bit_offset": 2,
            "data_type": "U1",
            "name": "xtalAbsent",
            "description": "RTC xtal has been determined to be absent"
          }
        ]
      },
      {
        "byte_offset": 3,
        "data_type": {
          "array_of": "CH",
          "count": 10
        },
        "name": "hwVersion",
        "description": "Zero-terminated hardware version string (same as that returned in the UBX-MON-VER message)"
      },
      {
        "byte_offset": 13,
        "data_type": {
          "array_of": "U1",
          "count": 9
        },
        "name": "reserved0",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 22,
        "data_type": {
          "repeated_group": {
            "count_field": "nPins",
            "block_length": 6
          }
        },
        "name": "pinStatus",
        "description": "Status for each I/O pin",
        "fields": [
          {
            "byte_offset": 0,
            "data_type": "U2",
            "name": "pinId",
            "description": "Identifier for the pin, including both external and internal pins."
          },
          {
            "byte_offset": 2,
            "data_type": "X2",
            "name": "pinMask",
            "description": "Pin mask",
            "bit_fields": [
              {
                "bit_offset": 0,
                "data_type": "U1",
                "name": "periphPIO",
                "description": "Pin is set to peripheral or PIO? 0=Peripheral 1=PIO"
              },
              {
                "bit_offset": 1,
                "data_type": "U3",
                "name": "pinBank",
                "description": "Bank the pin belongs to, where 0=A 1=B 2=C 3=D 4=E 5=F 6=G 7=H"
              },
              {
                "bit_offset": 4,
                "data_type": "U1",
                "name": "direction",
                "description": "Pin direction? 0=Input 1=Output"
              },
              {
                "bit_offset": 5,
                "data_type": "U1",
                "name": "value",
                "description": "Pin value? 0=Low 1=High"
              },
              {
                "bit_offset": 6,
                "data_type": "U1",
                "name": "vpManager",
                "description": "Used by virtual pin manager? 0=No 1=Yes"
              },
              {
                "bit_offset": 7,
                "data_type": "U1",
                "name": "pioIrq",
                "description": "Interrupt enabled? 0=No 1=Yes"
              },
              {
                "bit_offset": 8,
                "data_type": "U1",
                "name": "pioPullHigh",
                "description": "Using pull high resistor? 0=No 1=Yes"
              },
              {
                "bit_offset": 9,
                "data_type": "U1",
                "name": "pioPullLow",
                "description": "Using pull low resistor 0=No 1=Yes"
              }
            ]
          },
          {
            "byte_offset": 4,
            "data_type": "U1",
            "name": "VP",
            "description": "Virtual pin mapping"
          },
          {
            "byte_offset": 5,
            "data_type": "U1",
            "name": "reserved1",
            "description": "Reserved",
            "reserved": true
          }
        ]
      }
    ]
  }
}
