parts:
{
  "name": "UBX-NAV-PVT",
  "description": "Navigation position velocity time solution",
  "message_type": "periodic_polled",
  "class_id": 1,
  "message_id": 7,
  "payload_length": {
    "fixed": 92
  },
  "fields": [
    {
      "byte_offset": 0,
      "name": "iTOW",
      "data_type": "U4",
      "unit": "ms",
      "description": "GPS time of week of the navigation epoch."
    },
    {
      "byte_offset": 4,
      "name": "year",
      "data_type": "U2",
      "unit": "y",
      "description": "Year (UTC)"
    },
    {
      "byte_offset": 6,
      "name": "month",
      "data_type": "U1",
      "unit": "month",
      "description": "Month, range 1..12 (UTC)"
    },
    {
      "byte_offset": 7,
      "name": "day",
      "data_type": "U1",
      "unit": "d",
      "description": "Day of month, range 1..31 (UTC)"
    },
    {
      "byte_offset": 8,
      "name": "hour",
      "data_type": "U1",
      "unit": "h",
      "description": "Hour of day, range 0..23 (UTC)"
    },
    {
      "byte_offset": 9,
      "name": "min",
      "data_type": "U1",
      "unit": "min",
      "description": "Minute of hour, range 0..59 (UTC)"
    },
    {
      "byte_offset": 10,
      "name": "sec",
      "data_type": "U1",
      "unit": "s",
      "description": "Seconds of minute, range 0..60 (UTC)"
    },
    {
      "byte_offset": 11,
      "name": "valid",
      "data_type": "X1",
      "description": "Validity flags",
      "bit_fields": [
        {
          "name": "validDate",
          "bit_offset": 0,
          "num_bits": 1,
          "description": "1 = valid UTC Date"
        },
        {
          "name": "validTime",
          "bit_offset": 1,
          "num_bits": 1,
          "description": "1 = valid UTC time of day"
        },
        {
          "name": "fullyResolved",
          "bit_offset": 2,
          "num_bits": 1,
          "description": "1 = UTC time of day has been fully resolved"
        },
        {
          "name": "validMag",
          "bit_offset": 3,
          "num_bits": 1,
          "description": "1 = valid magnetic declination"
        }
      ]
    },
    {
      "byte_offset": 12,
      "name": "tAcc",
      "data_type": "U4",
      "unit": "ns",
      "description": "Time accuracy estimate (UTC)"
    },
    {
      "byte_offset": 16,
      "name": "nano",
      "data_type": "I4",
      "unit": "ns",
      "description": "Fraction of second, range -1e9.. 1e9 (UTC)"
    },
    {
      "byte_offset": 20,
      "name": "fixType",
      "data_type": "U1",
      "description": "GNSSfix Type",
      "mapping": {
        "0": "no fix",
        "1": "dead reckoning only",
        "2": "2D-fix",
        "3": "3D-fix",
        "4": "GNSS + dead reckoning combined",
        "5": "time only fix"
      }
    },
    {
      "byte_offset": 21,
      "name": "flags",
      "data_type": "X1",
      "description": "Fix status flags",
      "bit_fields": [
        {
          "name": "gnssFixOK",
          "bit_offset": 0,
          "num_bits": 1,
          "description": "1 = valid fix (i.e within DOP & accuracy masks)"
        },
        {
          "name": "diffSoln",
          "bit_offset": 1,
          "num_bits": 1,
          "description": "1 = differential corrections were applied"
        },
        {
          "name": "psmState",
          "bit_offset": 2,
          "num_bits": 3,
          "description": "Power save mode state",
          "mapping": {
            "0": "PSM is not active",
            "1": "Enabled (an intermediate state before Acquisition state)",
            "2": "Acquisition",
            "3": "Tracking",
            "4": "Power Optimized Tracking"
          }
        },
        {
          "name": "headVehValid",
          "bit_offset": 5,
          "num_bits": 1,
          "description": "1 = heading of vehicle is valid, only set if the receiver is in sensor fusion mode"
        },
        {
          "name": "carrSoln",
          "bit_offset": 6,
          "num_bits": 2,
          "description": "Carrier phase range solution status",
          "mapping": {
            "0": "no carrier phase range solution",
            "1": "carrier phase range solution with floating ambiguities",
            "2": "carrier phase range solution with fixed ambiguities"
          }
        }
      ]
    },
    {
      "byte_offset": 22,
      "name": "flags2",
      "data_type": "X1",
      "description": "Additional flags",
      "bit_fields": [
        {
          "name": "confirmedAvai",
          "bit_offset": 5,
          "num_bits": 1,
          "description": "1 = information about UTC Date and Time of Day validity confirmation is available"
        },
        {
          "name": "confirmedDate",
          "bit_offset": 6,
          "num_bits": 1,
          "description": "1 = UTC Date validity could be confirmed"
        },
        {
          "name": "confirmedTime",
          "bit_offset": 7,
          "num_bits": 1,
          "description": "1 = UTC Time of Day could be confirmed"
        }
      ]
    },
    {
      "byte_offset": 23,
      "name": "numSV",
      "data_type": "U1",
      "description": "Number of satellites used in Nav Solution"
    },
    {
      "byte_offset": 24,
      "name": "lon",
      "data_type": "I4",
      "scale": {
        "raw": "1e-7",
        "multiplier": 0.0000001
      },
      "unit": "deg",
      "description": "Longitude"
    },
    {
      "byte_offset": 28,
      "name": "lat",
      "data_type": "I4",
      "scale": {
        "raw": "1e-7",
        "multiplier": 0.0000001
      },
      "unit": "deg",
      "description": "Latitude"
    },
    {
      "byte_offset": 32,
      "name": "height",
      "data_type": "I4",
      "unit": "mm",
      "description": "Height above ellipsoid"
    },
    {
      "byte_offset": 36,
      "name": "hMSL",
      "data_type": "I4",
      "unit": "mm",
      "description": "Height above mean sea level"
    },
    {
      "byte_offset": 40,
      "name": "hAcc",
      "data_type": "U4",
      "unit": "mm",
      "description": "Horizontal accuracy estimate"
    },
    {
      "byte_offset": 44,
      "name": "vAcc",
      "data_type": "U4",
      "unit": "mm",
      "description": "Vertical accuracy estimate"
    },
    {
      "byte_offset": 48,
      "name": "velN",
      "data_type": "I4",
      "unit": "mm/s",
      "description": "NED north velocity"
    },
    {
      "byte_offset": 52,
      "name": "velE",
      "data_type": "I4",
      "unit": "mm/s",
      "description": "NED east velocity"
    },
    {
      "byte_offset": 56,
      "name": "velD",
      "data_type": "I4",
      "unit": "mm/s",
      "description": "NED down velocity"
    },
    {
      "byte_offset": 60,
      "name": "gSpeed",
      "data_type": "I4",
      "unit": "mm/s",
      "description": "Ground Speed (2-D)"
    },
    {
      "byte_offset": 64,
      "name": "headMot",
      "data_type": "I4",
      "scale": {
        "raw": "1e-5",
        "multiplier": 0.00001
      },
      "unit": "deg",
      "description": "Heading of motion (2-D)"
    },
    {
      "byte_offset": 68,
      "name": "sAcc",
      "data_type": "U4",
      "unit": "mm/s",
      "description": "Speed accuracy estimate"
    },
    {
      "byte_offset": 72,
      "name": "headAcc",
      "data_type": "U4",
      "scale": {
        "raw": "1e-5",
        "multiplier": 0.00001
      },
      "unit": "deg",
      "description": "Heading accuracy estimate (both motion and vehicle)"
    },
    {
      "byte_offset": 76,
      "name": "PDOP",
      "data_type": "U2",
      "scale": {
        "raw": "0.01",
        "multiplier": 0.01
      },
      "description": "Position DOP"
    },
    {
      "byte_offset": 78,
      "name": "flags3",
      "data_type": "X1",
      "description": "Additional flags",
      "bit_fields": [
        {
          "name": "invalidLlh",
          "bit_offset": 0,
          "num_bits": 1,
          "description": "1 = Invalid lon, lat, height and hMSL"
        }
      ]
    },
    {
      "byte_offset": 79,
      "name": "reserved0",
      "data_type": {
        "array_of": "U1",
        "count": 5
      },
      "reserved": true,
      "description": "Reserved"
    },
    {
      "byte_offset": 84,
      "name": "headVeh",
      "data_type": "I4",
      "scale": {
        "raw": "1e-5",
        "multiplier": 0.00001
      },
      "unit": "deg",
      "description": "Heading of vehicle (2-D), this is only valid when headVehValid is set, otherwise the output is set to the heading of motion"
    },
    {
      "byte_offset": 88,
      "name": "magDec",
      "data_type": "I2",
      "scale": {
        "raw": "1e-2",
        "multiplier": 0.01
      },
      "unit": "deg",
      "description": "Magnetic declination. Only supported in ADR 4.10 and later."
    },
    {
      "byte_offset": 90,
      "name": "magAcc",
      "data_type": "U2",
      "scale": {
        "raw": "1e-2",
        "multiplier": 0.01
      },
      "unit": "deg",
      "description": "Magnetic declination accuracy. Only supported in ADR 4.10 and later."
    }
  ]
}
